import { Link } from 'react-router-dom';
// import classes from "./NavBar.module.css";
import { useNavigate } from 'react-router-dom';
import React, { useContext } from 'react';
import { AuthContext } from '../Login/AuthContext';
// icon
// import { BiUserCircle } from "react-icons/bi";
const NavBar = () =>{
    const { status } = useContext(AuthContext);
    const handleLogout = () => {
        localStorage.setItem('loggedIn', 'false');
        localStorage.removeItem('token');
        navigate('/');
        // สามารถทำการอื่น ๆ เช่น ตั้งค่าต่าง ๆ ในการออกจากระบบที่คุณต้องการ
      };

    const navigate = useNavigate();
    return (
        <header>
        <nav>
          <ul>
            <p>
              <Link to="/" >หน้าหลัก</Link>
            </p>
            <p>
              <Link to="/Booking" >การจอง</Link>
            </p>
            <p>
              <Link to="/CusData" >ข้อมูลลูกค้า</Link>
            </p>
            <p>
              <Link to="/Housing">โครงการ</Link>
            </p>
  
{status ? (
        <p>
          Logged in!{' '}
          <button onClick={handleLogout}>Logout</button>
        </p>
      ) : (
        <>
          <p>
          <button className='button' onClick={() => { navigate('/LoginForm'); }}>เข้าสู่ระบบ</button>
          </p>
          <p>
          <button className='button' onClick={() => { navigate('/Register'); }}>สมัครสมาชิก</button>
          </p>
        </>
      )}

      
  
          </ul>
    
        </nav>
      </header>
        
    );
}
export default NavBar;
