import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

const Receipt = () => {
  const [books, setBooks] = useState([]);
  const Auth = useContext(AuthContext);

  useEffect(() => {
    loadBooking();
  }, []);

  const pdfUrl = 'https://drive.google.com/file/d/1--zdCZNH8oZnqVOegg3uCX7-S6xjFQhz/view?usp=sharing';

  const loadBooking = () => {
    axios.get('http://26.90.237.200:3000/book/read')
      .then(response => {
        setBooks(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handlePrint = () => {
    window.print();
  };

  return (
    <div className="container">
      <br />
      <div>
        <img src="/image/Receipt.png" alt="ภาพ PNG" className="receipt-image" />
        {books.map((book, idx) => (
          <div className="card" key={idx}>
            <div className="content">
              <p>เลขการจอง: {book.b_id}</p>
              <p>ราคา: {book.amount}</p>
              <p>วันที่จอง: {book.date}</p>

              <div></div>
              <p>บ้านเลขที่: {book.h_id}</p>
              <p>เลขบัตรประชาชนผู้จอง: {book.user_id}</p>
            </div>
            <div className="content">
              <button type="button" className="btn btn-primary" onClick={handlePrint}>
                แก้ไขข้อมูล
              </button>
              <br />
              <br />
              <br />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Receipt;
