import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AdminProfileEdit = () => {
  const API_URL = "http://26.90.237.200:3000/admin/edit";
  const navigate = useNavigate();

  const [profile, setProfile] = useState([]);
  const [newName, setNewName] = useState("");
  const [newAddress, setNewAddress] = useState("");
  const [newManagerName, setNewManagerName] = useState("");
  const [newManagerLastname, setNewManagerLastname] = useState("");
  const [newDownRate, setNewDownRate] = useState(0);
  const [newCompany, setNewCompany] = useState('');
  const [newPhone, setNewPhone] = useState('');
  const [newMDName, setNewMDName] = useState('');
  const [newMDNationality, setNewMDNationality] = useState('');
  const [newMDAddress, setNewMDAddress] = useState('');

  useEffect(() => {
    loadProfile();
  }, []);

  const loadProfile = () => {
    axios
      .get('http://26.90.237.200:3000/admin/read')
      .then(response => {
        setProfile(response.data);
        setNewDownRate(response.data[0]?.down_rate || 0);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const updateProfile = async () => {

    const formData = new FormData();
    formData.append("id", id || profile[0]?.he_id);
    formData.append("newName", newName || profile[0]?.house_plan);
    formData.append("newAddress", newAddress || profile[0]?.num_deed);
    formData.append("newMName", newManagerName || profile[0]?.num_survey);
    formData.append("newMLname", newManagerLastname || profile[0]?.land_area);
    formData.append("newDownRate", newDownRate || profile[0]?.area);
    formData.append("newCompany", newCompany || profile[0]?.h_price);
    formData.append("newPhone", newPhone || profile[0]?.book_price);
    formData.append("newMDName", newMDName || profile[0]?.h_note);
    formData.append("newMDNationality", newMDNationality || profile[0]?.hz_id);
    formData.append("newMDAddress", newMDAddress || profile[0]?.hz_id);
    formData.append("newImage", image); // ต้องแน่ใจว่าตัวแปร image เก็บค่าไฟล์รูปภาพจาก input file

    const data = {
      id: profile[0]?.he_id,
      newName: newName || profile[0]?.name,
      newAddress: newAddress || profile[0]?.address,
      newMName: newManagerName || profile[0]?.manager_name,
      newMLname: newManagerLastname || profile[0]?.manager_lastname,
      newDownRate: newDownRate || profile[0]?.down_rate,
      newCompany: newCompany || profile[0]?.company,
      newPhone: newPhone || profile[0]?.phone,
      newMDName: newMDName || profile[0]?.md_name,
      newMDNationality:newMDNationality || profile[0]?.md_nationality,
      newMDAddress:newMDAddress || profile[0]?.md_address,
    };

    try {
      const shouldEdit = window.confirm('คุณต้องการทำการแก้ไขหรือไม่?');
      if (shouldEdit) {
        const response = await axios.patch(API_URL, data, {
          headers: {
            'Content-Type': 'application/json'
          }
        });

        console.log(response.data);
        navigate('/AdminProfile');
      }
    } catch (error) {
      console.error(error);
      navigate('/Error');
    }
  };

  return (
    <div className="container">
       <h1>แก้ไขข้อมูลผู้จัดการ</h1>
      <form>
        {profile.map((val, idx) => (
          <div key={idx}>
            <div className="form-group">
              <label>ชื่อใหม่</label>
              <input
                type="text"
                className="form-control"
                value={newName || val.name}
                onChange={(e) => setNewName(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>ชื่อบริษัทใหม่</label>
              <input
                type="text"
                className="form-control"
                value={newCompany || val.company}
                onChange={(e) => setNewCompany(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>ที่อยู่ใหม่</label>
              <input
                type="text"
                className="form-control"
                value={newAddress || val.address}
                onChange={(e) => setNewAddress(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>ชื่อผู้จัดการใหม่</label>
              <input
                type="text"
                className="form-control"
                value={newManagerName || val.manager_name}
                onChange={(e) => setNewManagerName(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>นามสกุลผู้จัดการใหม่</label>
              <input
                type="text"
                className="form-control"
                value={newManagerLastname || val.manager_lastname}
                onChange={(e) => setNewManagerLastname(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>เบอร์โทร</label>
              <input
                type="text"
                className="form-control"
                value={newPhone || val.phone}
                onChange={(e) => setNewPhone(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>ชื่อกรรมการผู้จัดการ</label>
              <input
                type="text"
                className="form-control"
                value={newMDName || val.md_name}
                onChange={(e) => setNewMDName(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>สัญชาติกรรมการผู้จัดการ</label>
              <input
                type="text"
                className="form-control"
                value={newMDNationality || val.md_nationality}
                onChange={(e) => setNewMDNationality(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>ที่อยู่กรรมการผู้จัดการ</label>
              <input
                type="text"
                className="form-control"
                value={newMDAddress || val.md_address}
                onChange={(e) => setNewMDAddress(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>รูปภาพ</label>
              {/* <input
                type="text"
                className="form-control"
                value={newMDAddress || val.md_address}
                onChange={(e) => setNewMDAddress(e.target.value)}
              /> */}
            </div>
            <div className="form-group">
              <label>อัตราผ่อนใหม่</label>
              <input
                type="number"
                className="form-control"
                value={newDownRate}
                onChange={(e) => setNewDownRate(parseInt(e.target.value))}
              />
            </div>
          </div>
        ))}
        <br/> 
        <button type="button" className="btn btn-primary" onClick={updateProfile}>
          แก้ไขข้อมูล
        </button>
      </form>
    </div>
  );
};

export default AdminProfileEdit;
