import React, { useState, useEffect } from 'react';
import axios from 'axios';

const LoginPage = () => {
  const [authStatus, setAuthStatus] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    const config = {
      headers: {
        'Content-Type': 'application/json'
      }
    };

    const handleSubmit = async () => {
      try {
        const response = await axios.post("http://26.90.237.200:3000/login", { username, password });
        const token = response.data.token;
        localStorage.setItem('token', token);
        console.log(response.data);

        const authResponse = await axios.post('http://26.90.237.200:3000/authen', { username, password }, config);
        console.log(authResponse.data);
        if (authResponse.data.decoded.statusAuthen === 1) {
          alert('Authen success');
          setAuthStatus(authResponse.data.decoded.statusAuthen);
        } else {
          alert('Authen failed');
        }
      } catch (error) {
        alert('Authen error');
        console.error(error);
      }
    };

    handleSubmit();
  }, [username, password]);

  return (
    <div>
      <h1>Login</h1>
      {authStatus === 1 && <p>Authentication status: Success</p>}
      {authStatus === 0 && <p>Authentication status: Failed</p>}
    </div>
  );
};

export default LoginPage;
