import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate,useParams } from "react-router-dom";
// import { PDFDocument, rgb } from 'pdf-lib';
import dayjs from 'dayjs';
import fontkit from '@pdf-lib/fontkit';
import THSarabunBold from '../../fonts/THSarabunBold.ttf'; // ปรับปรุงพาธตามที่เหมาะสม
import PDF from './4_Comment.pdf'
import { degrees, PDFDocument, rgb } from 'pdf-lib';

async function modifyPdf() {
  // const url = PDF
  // const existingPdfBytes = await fetch(PDF).then(res => res.arrayBuffer())
  
  const pdfDoc = await PDFDocument.load(existingPdfBytes)
  // const helveticaFont = await pdfDoc.embedFont(StandardFonts.Helvetica)
  const fontBytes = await axios.get(THSarabunBold, { responseType: 'arraybuffer' }); 
  const font = await pdfDoc.embedFont(fontBytes.data); 

  const pages = pdfDoc.getPages()
  const firstPage = pages[0]
  const { width, height } = firstPage.getSize()
  firstPage.drawText('This text was added with JavaScript!', {
    x: 5,
    y: height / 2 + 300,
    size: 50,
    font,
    color: rgb(0.95, 0.1, 0.1),
    rotate: degrees(-45),
  })

  const pdfBytes = await pdfDoc.save()
}

const Receipt = () => {
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [houses, setHouse] = useState([]);
  const navigate = useNavigate();

  const { Bid } = useParams();
  // {
  //   data: JSON.stringify(data),
  //   headers: {
  //     'Content-Type': 'application/json'
  //   }
  // }
  useEffect(() => {
    loadBookingData();
  }, []);

  const loadBookingData = () => {
    axios.post(`http://26.90.237.200:3000/book/read/id`, { id: Bid })
      .then(response => {
        setBooks(response.data);
        // เมื่อได้รับข้อมูลการจองแล้ว ให้โหลดข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง
        if (response.data.length > 0) {
          loadUserData(response.data[0].user_id);
          loadHouse(response.data[0].h_id);
          // console.log(response.data[0].h_id)
        }
      })
      .catch(error => {
        console.error(error);
      });
  };
  
  const loadHouse = (houseId) => {
    axios.post(`http://26.90.237.200:3000/admin/house/read/id`, { id: houseId })
      .then(response => {
        setHouse(response.data);
        // console.log(response.data)
      })
      .catch(error => {
        console.error('เกิดข้อผิดพลาดในการดึงข้อมูล:', error);
      });
  };
  
  const loadUserData = (userId) => {
    axios.post(`http://26.90.237.200:3000/user/read/id`, { id: userId })
      .then(response => {
        setUsers(response.data);
        // console.log(response.data)
      })
      .catch(error => {
        console.error(error);
      });
  };

  const generatePdf = async () => {
    try {
      const pdfDoc = await PDFDocument.create();
      pdfDoc.registerFontkit(fontkit);
      const fontBytes = await axios.get(THSarabunBold, { responseType: 'arraybuffer' }); 
      const font = await pdfDoc.embedFont(fontBytes.data); 
      const page = pdfDoc.addPage([600, 800]);
      let y = 780;
  
      // Assuming the user data and house data is related to the current book
      const relatedUser = users[0];
      const relatedHouse = houses[0];
  
      // You might want to adjust the x, y values and the text size to fit your needs
      page.drawText(`วันที่: ${dayjs().format('YYYY-MM-DD')}`, { x: 50, y: y, size: 12, font, color: rgb(0, 0, 0), });
      page.drawText(`ชื่อลูกค้า: ${relatedUser.user_name} ${relatedUser.user_lastname}`, { x: 50, y: y - 20, size: 12, font, color: rgb(0, 0, 0), });
      page.drawText(`อายุ: ${relatedUser.user_age} ปี`, { x: 50, y: y - 40, size: 12, font, color: rgb(0, 0, 0), });
      page.drawText(`โทรศัพท์บ้าน: ${relatedUser.user_phone}`, { x: 50, y: y - 60, size: 12, font, color: rgb(0, 0, 0), });
      page.drawText(`แปลงเลขที่: ${relatedHouse.id}`, { x: 50, y: y - 80, size: 12, font, color: rgb(0, 0, 0), });
      page.drawText(`พื้นที่: ${relatedHouse.land_area} ตรว.`, { x: 50, y: y - 100, size: 12, font, color: rgb(0, 0, 0), });
      page.drawText(`จอง (วันที่ ${dayjs().format('YYYY-MM-DD')}) เป็นเงิน ${relatedHouse.book_price} บาท`, { x: 50, y: y - 120, size: 12, font, color: rgb(0, 0, 0), });
  
      // ทำแบบเดียวกับส่วนอื่น ๆ ที่ต้องการแสดงผล
  
      const pdfBytes = await pdfDoc.save();
      const pdfUrl = URL.createObjectURL(new Blob([pdfBytes], { type: 'application/pdf' }));
  
      window.open(pdfUrl, 'blank');
      navigate('/Booking');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container">
      <br />
      <div>
        {books.map((book) => (
          <div className="card" key={book.b_id}>
            <div className="content">
              <p>เลขการจอง : {book.b_id}</p>
              {/* แสดงข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง */}
              {users.length > 0 ? (
                users.map((user) => (
                  // แสดงข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง
                  <div key={user.id}>
                    <p>ชื่อ-สกุล : {user.user_name} {user.user_lastname}</p>
                    <p >เบอร์โทร : {user.user_phone}</p>
                    <p>อายุ : {user.user_age}</p>
                  </div>
                  ))) : (<p>ไม่พบข้อมูลผู้ใช้</p>)}
                  
              <p>วันที่จอง : {dayjs(book.date).format('YYYY-MM-DD')}</p>
              <p>บ้านเลขที่ : {book.h_id}</p>
              {houses.length > 0 ? (
                houses.map((house) => (
                  // แสดงข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง
                  <div key={house.id}>
                    <p>ขนาดพื้นที่ดิน : {house.land_area} ตารางวา </p>
                    <p>แปลนบ้าน : {house.house_plan} </p>
                    <p>ยอดผ่อนดาว : {house.book_price} บาท</p>
                    <p>ค่าจอง : {house.book_price} บาท</p>
                  </div>
                  ))) : (<p>ไม่พบข้อมูลผู้ใช้</p>)}
         
            </div>
          </div>
        ))}
      </div>
      <br />
      <button type="button" className="btn btn-primary" onClick={modifyPdf}>
      บันทึกเป็น PDF
    </button>
    </div>
  );
};

export default Receipt;
