import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../Login/AuthContext';
import { NumericFormat } from 'react-number-format';

const ShowHousing = () => {
  const Auth = useContext(AuthContext);
  const [houses, setHouses] = useState([]);
  const [zones, setZones] = useState([]);
  const [selectedType, setSelectedType] = useState(null);
  // const navigate = useNavigate();

  useEffect(() => {
    loadHouses();
    loadHouseTypes();
  }, []);

  const loadHouses = () => {
    axios
      .get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const loadHouseTypes = () => {
    axios
      .get('http://26.90.237.200:3000/admin/house_zone/read')
      .then(response => {
        setZones(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleShowAllClick = () => {
    axios
      .get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouses(response.data);
        setSelectedType(null);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleZoneClick = (zone) => {
    const data = { id: zone.hz_id };
    setSelectedType(data);
    axios
      .post('http://26.90.237.200:3000/admin/house/read/zone', data)
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleDeleteClick = (house) => {
    const confirmDelete = window.confirm('คุณต้องการลบบ้านนี้หรือไม่?');
    if (confirmDelete) {
      axios
        .delete(`http://26.90.237.200:3000/admin/house/delete/${house.h_id}`)
        .then(response => {
          alert('ลบบ้านสำเร็จ');
          // ดำเนินการหลังจากลบบ้านสำเร็จ (เช่น อัปเดตรายการบ้าน)
          loadHouses(); // เรียกโหลดรายการบ้านอีกครั้งหลังจากลบ
        })
        .catch(error => {
          console.error(error);
          alert('เกิดข้อผิดพลาดในการลบบ้าน');
        });
    }
  };

  return (
    <div className="container">
      <div className="btn-group" role="group" aria-label="Basic radio toggle button group">
        <input type="radio" className="btn-check" name="btnradio" id="showAll" autoComplete="off" checked={!selectedType} onChange={handleShowAllClick} />
        <label className="btn btn-outline-primary" htmlFor="showAll">ทั้งหมด</label>
        {zones.map((zone, idx) => (
          <React.Fragment key={idx}>
            <input type="radio" className="btn-check" name="btnradio" id={`zone${zone.hz_id}`} autoComplete="off" checked={selectedType && selectedType.id === zone.hz_id} onChange={() => handleZoneClick(zone)} />
            <label className="btn btn-outline-primary" htmlFor={`zone${zone.hz_id}`}>{zone.name}</label>
          </React.Fragment>
        ))}
      </div>

      {Auth.isLoggedIn && (
        <div className="d-flex justify-content-end">
          <Link to="/HousingAdd" className="btn btn-success">เพิ่ม</Link>
        </div>
      )}

      <br />

      <div>
        {houses.map((house, idx) => (
          <div className="card" key={idx}>
            <div className="content">
              <p>โซน {house.name}</p>
              <p>บ้านเลขที่ {house.h_id}</p>
              <p>แปลนบ้าน {house.house_plan}</p>
              <div className="content">
                <p>IMG อันนี้จะใส่เป็นรูปบ้านนะแต่ยังไม่ได้ทำ</p>
              </div>
              <p>ขนาดพื้นที่ดิน {house.land_area} ตารางวา</p>
              <p>ขนาดพื้นที่ใช้สอย {house.area} ตารางเมตร</p>
              <NumericFormat
                value={house.h_price}
                allowLeadingZeros
                thousandSeparator=","
                displayType="text"
                renderText={(value) => <p>{value} บาท</p>}
              />
            </div>
            <div className="content">
              <Link to={`/HousingDetails/${house.h_id}`} className="btn btn-primary">
                ดูรายละเอียด
              </Link>
              <br />
              {Auth.isLoggedIn && (
                <div className="content">
                  <Link to={`/HousingEdit/${house.h_id}`} className="btn btn-warning">
                    แก้ไข
                  </Link>
                  <button className="btn btn-danger" onClick={() => handleDeleteClick(house)}>
                    ลบ
                  </button>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ShowHousing;
