import axios from 'axios';

const loginUser = async (username, password) => {
  try {
    const response = await axios.post("http://26.90.237.200:3000/login", { username, password });
    const settoken = response.data.token;
    localStorage.setItem('token', settoken);
    // console.log(response.data);
    const token = localStorage.getItem('token');
    const config = {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    };
    const authResponse = await axios.post('http://26.90.237.200:3000/authen', { username, password },config);
    console.log(authResponse.data);
    
    // console.log(authResponse.data.decoded.statusAuthen);
    if (authResponse.data.decoded.statusAuthen === 1) {
      alert('Authen success');
      return authResponse.data.decoded.statusAuthen;
      
    } else {
      alert('Authen failed');
      return 0;
    }
  } catch (error) {
    // localStorage.removeItem('token');
    alert('Authen error');
    console.error(error);
    return 0;
  }
};

export default loginUser;
