import React, { useEffect, useState,useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

const User = () => {
  const [users, setUsers] = useState([]);
  const Auth = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://26.90.237.200:3000/user/read')
      .then(response => {
        setUsers(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  return (
    <div className="container">
        <h1>ข้อมูลผู้ลูกค้า</h1>
      <div>
        {users.map((user, idx) => (
          <div className="card" key={idx}>
              {Auth.isLoggedIn && (
                <div className="d-flex justify-content-end">
                    <Link to={`/EditUser/${user.user_id}`} className="btn btn-warning">
                    แก้ไข
                  </Link>
                </div>
            )}
            <div className="content">
              <p>เลขบัตรประชาชน: {user.user_id}</p>
              <p>ชื่อ: {user.user_name}</p>
              <p>นามสกุล: {user.user_lastname}</p>
              <p>ที่อยู่: {user.user_address}</p>
              <p>อายุ: {user.user_age}</p>
              <p>เบอร์โทร: {user.user_phone}</p>
              <p>ที่อยู่อีเมล: {user.user_email}</p>
              {/* <p>รหัสผ่าน: {user.user_password}</p> */}
              <p>สถานะการยืนยัน: {user.status_authen}</p>
            </div>
            <div className="content">
              <br />
              <br />
              <br />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default User;
