import React, { createContext, useEffect, useState } from 'react';
import axios from 'axios';

const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [status, setStatus] = useState(localStorage.getItem('userStatus') || false);
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('loggedIn') === 'true');
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState([]);

  useEffect(() => {
    const loggedInStatus = localStorage.getItem('loggedIn') === 'true';
    const userStatus = localStorage.getItem('userStatus');
    const user = localStorage.getItem('user');
    setIsLoggedIn(loggedInStatus);
    setStatus(userStatus);
    setUser(user)
    setProfile(profile)
  }, []);

  useEffect(() => {
    loadProfile();
  }, []);

  const loadProfile = () => {
    axios
      .get('http://26.90.237.200:3000/admin/read')
      .then(response => {
        setProfile(response.data);
        setNewDownRate(response.data[0]?.down_rate || 0);

      })
      .catch(error => {
        console.error(error);
      });
  };

  

  return (
    <AuthContext.Provider value={{ status, isLoggedIn, setIsLoggedIn, setStatus ,user, setUser, profile,setProfile}}>
      {children}
    </AuthContext.Provider>
  );
};

export { AuthContext, AuthProvider };
