import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
// import { NumericFormat } from 'react-number-format';
import { useNavigate } from 'react-router-dom';

const ShowHousing = () => {
  const Auth = useContext(AuthContext);
  const [houses, setHouses] = useState([]);
  const [types, setTypes] = useState([]);
  const [selectedType, setSelectedType] = useState(null); // เพิ่มตัวแปร state เพื่อเก็บประเภทที่ถูกเลือก
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });

    axios.get('http://26.90.237.200:3000/admin/house_type/read')
      .then(response => {
        setTypes(response.data);
        console.log(response.data)
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleTypeClick = (type) => {
    setSelectedType(type); // เมื่อประเภทถูกคลิก กำหนดค่าประเภทที่ถูกเลือกให้กับตัวแปร state
  };

  const filteredHouses = selectedType
    ? houses.filter(house => house.name === selectedType.name) // กรองข้อมูลโดยใช้ชื่อประเภท
    : houses; // ถ้าไม่ได้เลือกประเภท แสดงทั้งหมด

  return (
    <div className="container">
      <button className="btn btn-primary" onClick={() => { navigate('/Housing'); }}>ทั้งหมด</button>
      {types.map((type, idx) => (
        <button
          className={`btn btn-primary ${type === selectedType ? 'active' : ''}`}
          key={idx}
          onClick={() => handleTypeClick(type)}
        >
          โซน {type.name}
        </button>
      ))}

      {Auth.isLoggedIn && (
        <button className="btn btn-success" onClick={() => { navigate('/TypeAdd'); }}> เพิ่ม </button>
      )}
      {Auth.isLoggedIn && (
        <button className="btn btn-warning" onClick={() => { navigate('/TypeEdit'); }}> แก้ไขประเภท </button>
      )}
      {Auth.isLoggedIn && (
        <button className="btn btn-danger" onClick={() => { navigate('/TypeDel'); }}> ลบ </button>
      )}
      {Auth.isLoggedIn && (
        <div className="d-flex justify-content-end">
          <button className="btn btn-success" onClick={() => { navigate('/HousingAdd'); }}>เพิ่ม</button>
        </div>
      )}

      <div>
        {filteredHouses.map((house, idx) => (
          <div className="card" key={idx}>
            {/* ... */}
          </div>
        ))}
      </div>
    </div>
  );
}

export default ShowHousing;
