import React, { useState } from 'react';
import axios from 'axios';
// import { useNavigate } from 'react-router-dom';

const HousingDelete = () => {
  // const navigate = useNavigate();
  const [id, setId] = useState('');
console.log(id)

const deleteHouse = async () => {
  try {
    const response = await axios.delete(`http://26.90.237.200:3000/admin/house/delete/${id}`);
    console.log(response.data);
  } catch (error) {
    console.error(error);
  }
};

  return (
    <div className="container">
      <form>
        <div className="mb-3">
          <label htmlFor="id" className="form-label">
            รหัส
          </label>
          <input
            type="text"
            className="form-control"
            id="id"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </div>
        <button type="button" className="btn btn-danger" onClick={deleteHouse}>
          ลบข้อมูล
        </button>
      </form>
    </div>
  );
};

export default HousingDelete;
