import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const ZoneEdit = () => {
  const House_API = "http://26.90.237.200:3000/admin/house_zone/edit";
  const navigate = useNavigate();
  
  const [zone, setZone] = useState([]);
  const [newName, setNewName] = useState("");
  const { Zid } = useParams();

  useEffect(() => {
    loadZoneData();
  }, []);

  const loadZoneData = () => {
    axios.get('http://26.90.237.200:3000/admin/house_zone/read')
      .then(response => {
        setZone(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const updateZone = async () => {
    const data = {
      id: Zid ,
      newName: newName || zone[0]?.name,
    };
    console.log()
    try {
      const shouldEdit = window.confirm('คุณต้องการแก้ไขหรือไม่?');
      if (shouldEdit) {
        const response = await axios.patch(House_API, data);
        console.log(response.data);
        alert('แก้ไขสำเร็จ');
        navigate('/Zoneing');
      }
    } catch (error) {
      console.error(error);
      alert('เกิดข้อผิดพลาดในการแก้ไข');
      navigate('/Error');
    }
  };
  
  return (
    <div className="container">
      <h1>แก้ไขโซนที่ดิน</h1>
      <form>
        <div className="form-group">
          <label>ชื่อใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newName || zone[0]?.name}
            onChange={(e) => setNewName(e.target.value)}
          />
        </div>
        <br/>
        <button type="button" className="btn btn-primary" onClick={updateZone}>
          แก้ไขข้อมูล
        </button>
      </form>
    </div>
  );
};

export default ZoneEdit;
