import axios from 'axios';

const loginUser = async (username, password) => {
  const token = localStorage.getItem('token');
  console.log(token)
  const config = {
    headers: {
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json'
    }
  };
  try {
    const response = await axios.post("http://26.90.237.200:3000/login", { username, password });
    const token = response.data.token;
    localStorage.setItem('token', token);
    // console.log(response.data);

    const authResponse = await axios.post('http://26.90.237.200:3000/authen', { username, password },config);
    console.log(authResponse.data);
    if (authResponse.data.decoded.statusAuthen === 1) {
      alert('Authen success');
      return authResponse.data.decoded.statusAuthen;
      
    } else {
      alert('Authen failed');
      return 0;
    }
  } catch (error) {
    alert('Authen error');
    console.error(error);
    return 0;
  }
};

export default loginUser;
