import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import loginUser from './Login';

const LoginForm = () => {
  const navigate = useNavigate();
  const [authStatus, setAuthStatus] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const status = await loginUser(username, password);
    setAuthStatus(status);
    console.log(status)

    const authResponse = await loginUser(username, password);
    if (authResponse.statusAuthen === 1) {
      console.log(authResponse.statusAuthen)
      // สร้าง session หรือตัวแปรเก็บข้อมูลการเข้าสู่ระบบที่ต้องการ
      // เช่น ReactSession.set('token', authResponse.token);
      alert('Authentication success');
      setAuthStatus(authResponse.statusAuthen);
      navigate('/dashboard'); // นำผู้ใช้ไปยังหน้าต่าง ๆ ที่ต้องการหลังจากการเข้าสู่ระบบสำเร็จ
    } else {
      alert('Authentication failed');
      setAuthStatus(authResponse.statusAuthen);
    }
  };

  return (
    <div>
      <h1>Login Form</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            name="username"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            name="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div>
          <button type="submit">Login</button>
        </div>
      </form>
      {authStatus === 1 && <p>Authentication status: Success</p>}
      {authStatus === 0 && <p>Authentication status: Failed</p>}
    </div>
  );
};

export default LoginForm;
