import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import React, { useContext } from 'react';
import { AuthContext } from '../Login/AuthContext';
const [isAdmin, setIsAdmin] = useState(false);
const [isUser, setIsUser] = useState(false);

// ตรวจสอบสิทธิ์ผู้ใช้และอัปเดตสถานะ
if (user.role === 'admin') {
  setIsAdmin(true);
} else if (user.role === 'user') {
  setIsUser(true);
}
const NavBar = () => {
  const { status } = useContext(AuthContext);
  const handleLogout = () => {
    localStorage.setItem('loggedIn', 'false');
    localStorage.removeItem('token');
    navigate('/');
  };

  const navigate = useNavigate();

  return (
    <header>
      <nav className="navbar navbar-expand-lg navbar-dark " style={{ backgroundColor: '#47345F' }}>
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">Logo</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav"
            aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav mx-auto">
            <li className="nav-item">
                <Link className="nav-link" to="/">หน้าหลัก</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Booking">การจอง</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/CusData">ข้อมูลลูกค้า</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Housing">โครงการ</Link>
              </li>
            </ul>
            <ul className="navbar-nav ms-auto">
              <li className="nav-item me-3">
                {status ? (
                  <p>
                    <button onClick={handleLogout} className="btn btn-outline-light">Logout</button>
                  </p>
                ) : (
                  <>
                  <button className="btn btn-outline-light"> <Link className="nav-link" to="/LoginForm">เข้าสู่ระบบ</Link> </button>
                  <button className="btn btn-outline-light"> <Link className="nav-link" to="/Register">สมัครสมาชิก</Link> </button>
                  </>
                )}
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </header>
  );
};

export default NavBar;
