import React, { useState, useContext } from 'react';
import { AuthContext } from './AuthContext';
import loginUser from './Login';
import { useNavigate } from 'react-router-dom';

const LoginForm = () => {
  const { setStatus } = useContext(AuthContext);
  const navigate = useNavigate();
  // const [authStatus, setAuthStatus] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const status = await loginUser(username, password);
    setStatus(status);
    // setAuthStatus(status);
    console.log(status);
    if (status === 1) {
      localStorage.setItem('loggedIn', 'true');
       navigate('/Housing');
    } else {
      localStorage.setItem('loggedIn', 'false');
       navigate('/');
    }
  };
  const handleLogout = () => {
    localStorage.removeItem('loggedIn', 'true');
    localStorage.removeItem('token');
    // navigate('/');
    
    // สามารถทำการอื่น ๆ เช่น ตั้งค่าต่าง ๆ ในการออกจากระบบที่คุณต้องการ
  };

  return (
    <div className="container">
    <h1>Login Form</h1>
    <form onSubmit={handleSubmit}>
      <div className="mb-3">
        <label htmlFor="username" className="form-label">Username</label>
        <input
          type="text"
          className="form-control"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="password" className="form-label">Password</label>
        <input
          type="password"
          className="form-control"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <button type="submit" className="btn btn-primary">Login</button>
      </div>
      <div className="mb-3">
        <button type="submit" onClick={handleLogout} className="btn btn-primary">Logout</button>
      </div>
    </form>
  </div>
  );
};

export default LoginForm;
