import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AdminProfileEdit = () => {
  const API_URL = "http://26.90.237.200:3000/admin/edit";
  const navigate = useNavigate();

  const [profile, setProfile] = useState([]);
  const [newName, setNewName] = useState("");
  const [newAddress, setNewAddress] = useState("");
  const [newManagerName, setNewManagerName] = useState("");
  const [newManagerLastname, setNewManagerLastname] = useState("");
  const [newDownRate, setNewDownRate] = useState(0);
  const [newCompany, setNewCompany] = useState('');
  const [newPhone, setNewPhone] = useState('');
  const [newMDName, setNewMDName] = useState('');
  const [newMDNationality, setNewMDNationality] = useState('');
  const [newMDAddress, setNewMDAddress] = useState('');
  const [image, setImage] = useState('');

  useEffect(() => {
    loadProfile();
  }, []);

  const loadProfile = () => {
    axios
      .get('http://26.90.237.200:3000/admin/read')
      .then(response => {
        setProfile(response.data);
        setNewDownRate(response.data[0]?.down_rate || 0);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const updateProfile = async () => {
    const formData = new FormData();
    formData.append('id', 1);
    formData.append('newName', newName || profile[0]?.name);
    formData.append('newAddress', newAddress || profile[0]?.address);
    formData.append('newMName', newManagerName || profile[0]?.manager_name);
    formData.append('newMLname', newManagerLastname || profile[0]?.manager_lastname);
    formData.append('newDownRate', newDownRate || profile[0]?.down_rate);
    formData.append('newCompany', newCompany || profile[0]?.company);
    formData.append('newPhone', newPhone || profile[0]?.phone);
    formData.append('newMDName', newMDName || profile[0]?.md_name);
    formData.append('newMDNationality', newMDNationality || profile[0]?.md_nationality);
    formData.append('newMDAddress', newMDAddress || profile[0]?.md_address);
    formData.append('newImage', image);

console.log(formData.image)
    try {
      const shouldEdit = window.confirm('คุณต้องการทำการแก้ไขหรือไม่?');
      if (shouldEdit) {
        const response = await axios.patch(API_URL, formData,  {
          headers: {
            'Content-Type': 'multipart/form-data', // ตั้งค่า Content-Type เป็น multipart/form-data
          }
        });

        console.log(response.data);
        navigate('/AdminProfile');
      }
    } catch (error) {
      console.error(error);
      navigate('/Error');
    }
  };

  return (
    <div className="container">
       <h1>แก้ไขข้อมูลผู้จัดการ</h1>
      <form>
        {profile.map((val, idx) => (
          <div key={idx}>
             <div>
            <label htmlFor="newName" className="form-label">
            ชื่อโครงการ
            </label>
            <input
               maxLength="20"
              type="text"
              className="form-control"
              id="newName"
              value={newName || val.name}
              onChange={e => setNewName(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="housePlan" className="form-label">
            ชื่อบริษัทใหม่
            </label>
            <input
             maxLength="25"
              type="text"
              className="form-control"
              id="housePlan"
              value={housePlan || val.manager_name}
              onChange={e => setHousePlan(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="numDeed" className="form-label">
            ที่อยู่ใหม่
            </label>
            <input
             maxLength="6"
              type="text"
              className="form-control"
              id="numDeed"
              value={numDeed || val.address}
              onChange={e => setNumDeed(e.target.value)}
            />
          </div>

          <div>
            <label htmlFor="numSurvey" className="form-label">
            นามสกุลผู้จัดการใหม่
            </label>
            <input
              maxLength="5"
              type="text"
              className="form-control"
              id="numSurvey"
              value={numSurvey || val.h_id}
              onChange={e => setNewNumSurvey(e.target.value)}
            />
          </div>

          <div>
            <label htmlFor="landArea" className="form-label">
            เบอร์โทร
            </label>
            <input
              maxLength="10"
              type="number"
              className="form-control"
              id="landArea"
              value={landArea || val.land_area}
              onChange={e => setLandArea(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="area" className="form-label">
            ชื่อกรรมการผู้จัดการ
            </label>
            <input
              maxLength="10"
              type="number"
              className="form-control"
              id="area"
              value={area || val.area}
              onChange={e => setArea(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="price" className="form-label">
            ที่อยู่กรรมการผู้จัดการ
            </label>
            <input
            maxLength="20"
              type="number"
              className="form-control"
              id="price"
              value={price || val.h_price}
              onChange={e => setPrice(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="newBookingPrice" className="form-label">
            อัตราผ่อนใหม่
            </label>
            <input
            maxLength="20"
              type="number"
              className="form-control"
              id="newBookingPrice"
              value={BookingPrice || val.book_price}
              onChange={e => setBookingPrice(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="image" className="form-label">
            รูปภาพ Logo บริษัท
            </label>
            <input
              type="file"
              className="form-control"
              id="image"
              onChange={e => setImage(e.target.files[0])}
            />
          </div>
          </div>
        ))}
        <br/> 
        <button type="button" className="btn btn-primary" onClick={updateProfile}>
          แก้ไขข้อมูล
        </button>
      </form>
    </div>
  );
};

// โครงการ : พวงเพชร 4

// ชื่อบริษัท : บริษัท ศิวิลัยแลนด์ ขอนแก่น จำกัด

// ที่อยู่ : 44 หมู่ 11 ตำบลบ้านเป็ด อำเภอเมือง จังหวัดขอนแก่น 40000

// ชื่อ : นางรุ่งรัตน์

// นามสกุล : ชูศักดิ์

// เบอร์โทร : 043468292

// อัตตราผ่อน : 20 %

// เงินส่วนกลาง : 0

// รูป : 20 %

export default AdminProfileEdit;
