import React, { useState } from "react";
import axios from "axios";

const HousingEdit = () => {
  const House_API = "http://26.90.237.200:3000/admin/house_type/edit";

  const [id, setId] = useState("");
  const [newName, setNewName] = useState("");
  const [newUsableArea, setNewUsableArea] = useState("");
  const [newStartPrice, setNewStartPrice] = useState("");

  const updateEmp = async () => {
    const data = {
      id: id,
      newName: newName,
      newUsableArea: newUsableArea,
      newStartPrice: newStartPrice
    };

    try {
      const response = await axios.patch(House_API, data);
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container">
    <form>
      <div className="mb-3">
        <div>
          <label htmlFor="id" className="form-label">รหัส</label>
          <input
            type="text"
            className="form-control"
            id="id"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="newName" className="form-label">ชื่อใหม่</label>
          <input
            type="text"
            className="form-control"
            id="newName"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="newUsableArea" className="form-label">พื้นที่ใช้สอยใหม่</label>
          <input
            type="text"
            className="form-control"
            id="newUsableArea"
            value={newUsableArea}
            onChange={(e) => setNewUsableArea(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="newStartPrice" className="form-label">ราคาเริ่มต้นใหม่</label>
          <input
            type="text"
            className="form-control"
            id="newStartPrice"
            value={newStartPrice}
            onChange={(e) => setNewStartPrice(e.target.value)}
          />
        </div>
      </div>

      <button type="button" className="btn btn-primary" onClick={updateEmp}>
        แก้ไขข้อมูล
      </button>
    </form>
  </div>
  );
};

export default HousingEdit;
