import React from 'react';
import './Login.css';
import { useState, useEffect} from 'react';
import axios from 'axios';
// import { useNavigate } from 'react-router-dom';

const Login = (porps) => {

  const handleSubmit = e => {
    // Prevent the default submit and page reload
    e.preventDefault()

  // useEffect(()) => {

  // }
  const token = localStorage.getItem('token')
    // Handle validations
    axios
      .post("http://26.90.237.200:3000/login", { username, password }, { headers: {"Authorization" : `Bearer ${token}`}})
      // .then((response) => localStorage.setItem("token", response.data.token)) 
      .then(response => {
        console.log(response)
      })
      .then(status => {
        if (status == 202) {
          alert('Login Success')
  
          // localStorage.getItem('token', data.token)
          
        }   else {
     
          alert('Login failed')
        }
      })
  }


  const handleLogin = async () => {
    try {
      const response = await axios.post('login-endpoint', {
        // Include any necessary login credentials
        username: 'your-username',
        password: 'your-password',
      });
  
      // Extract the token from the response
      const { token } = response.data;
  
      // Save the token in your application state or localStorage
      // For example, if you're using React state:
      
      setState({ token });
      // or if you're using localStorage:
      localStorage.setItem('token', token);
  
      // Perform any additional actions, such as redirecting the user
    } catch (error) {
      // Handle error responses
      console.error('Login error:', error);
    }
  };
  const [username, setUsername] = useState()
  const [password, setPassword] = useState()


  return (
    <div>
      <form action="" id="login" method="post" onSubmit={handleSubmit}>
        <h1>Login</h1>
        <p className="item">
          <label for="username"> Username </label>
          <input
            type="username"
            name="username"
            id="username"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
        </p>
        <p className="item">
          <label for="password"> Password </label>
          <input
            type="password"
            name="password"
            id="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
        </p>
        <p className="item">
          <input type="submit" value="Login" />
        </p>
      </form>
    </div>
  )

  //   return (
  //   <div>
  //   <form action="" id="login" method="post">
  //     <h1>Login</h1>
  //     <p className="item">
  //       <label for="username"> Username </label>
  //       <input type="username" name="username" id="username" />
  //     </p>
  //     <p className="item">
  //       <label for="password"> Password </label>
  //       <input type="password" name="password" id="password" />
  //     </p>
  //     <p className="item">
  //       <input type="submit" value="Login" />
  //     </p>
  //   </form>
  // </div>
  //   );
  };

  export default Login;
