import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { AuthContext } from '../Login/AuthContext';
import { NumericFormat } from 'react-number-format';

const ShowHousing = () => {
  const Auth = useContext(AuthContext);
  const [houses, setHouses] = useState([]);
  const [NCP, setNCP] = useState([]);

  // const [zones, setZones] = useState([]);
  // const [selectedType, setSelectedType] = useState(null);
  // const navigate = useNavigate();

  useEffect(() => {
    // loadHouses();
    // loadHouseTypes();
    loadNCP();
  }, []);

 
  const loadNCP = () => {
    axios
      .get(' http://26.90.237.200:3000/notice-common-payment/read')
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };


  // const loadHouses = () => {
  //   axios
  //     .get('http://26.90.237.200:3000/admin/house/read')
  //     .then(response => {
  //       setHouses(response.data);
  //     })
  //     .catch(error => {
  //       console.error(error);
  //     });
  // };

  // const loadHouseTypes = () => {
  //   axios
  //     .get('http://26.90.237.200:3000/admin/house_zone/read')
  //     .then(response => {
  //       setZones(response.data);
  //     })
  //     .catch(error => {
  //       console.error(error);
  //     });
  // };


  const statusBill = (status) => {
    switch (status) {
      case 0:
        return { message: ' จ่ายแล้ว ', colorClass: 'text-success' };
        case 3:
          return { message: ' ยังไม่จ่าย ', colorClass: 'text-danger' };
      default:
        return { message: ' ไม่ทราบสถานะ', colorClass: '' };
    }
  };

  return (
    <div className="container">
        <h1> ข้อมูลค่าส่วนกลาง </h1>
  <br />
      <br />
      <div className="row">
        {houses.map((house, idx) => (
          <div className="col-4" key={idx}>
            <div className="card " >
           
              <div className="card-body">
                <h5 className="card-title">เลขที่บิล {house.h_id}</h5>
                <p className="card-text">บ้านเลขที่ : {house.h_id} โซน :{house.name}</p>
                <p className="card-text">แปลนบ้าน : {house.house_plan}</p>
                <p className="card-text">
                  สถานะ : <span className={`fw-bold ${statusBill(house.status).colorClass}`}>
                    {statusBill(house.status).message}
                  </span>
                </p>

                {Auth.isLoggedIn && (
                  <div> 
                    <p className="card-text">หมายเหตุ: {house.h_note}</p>
                  </div>
                )}

              </div>
            </div>
            <br />
          </div>
        ))}
      </div>
    </div>
  );
}

export default ShowHousing;
