import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from "react-router-dom";
import fontkit from '@pdf-lib/fontkit';
import THSarabunBold from '../../fonts/THSarabunBold.ttf'; // ปรับปรุงพาธตามที่เหมาะสม
import PDF from './4_Comment.pdf'
import { PDFDocument, rgb } from 'pdf-lib';
import dayjs from 'dayjs';
import 'dayjs/locale/th';
import customParseFormat from 'dayjs/plugin/customParseFormat';
// import advancedFormat from 'dayjs-plugin-advancedFormat';
// import buddhistEra from 'dayjs-plugin-buddhistEra';
dayjs.extend(customParseFormat);
dayjs.locale('th'); 

const Receipt = () => {
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [houses, setHouse] = useState([]);

  const { Bid } = useParams();

  useEffect(() => {
    loadBookingData();
  }, []);

  const loadBookingData = () => {
    axios.post(`http://26.90.237.200:3000/book/read/id`, { id: Bid })
      .then(response => {
        setBooks(response.data);
        // เมื่อได้รับข้อมูลการจองแล้ว ให้โหลดข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง
        if (response.data.length > 0) {
          loadUserData(response.data[0].user_id);
          loadHouse(response.data[0].h_id);
          // console.log(response.data[0].h_id)
        }
      })
      .catch(error => {
        console.error(error);
      });
  };
  
  const loadHouse = (houseId) => {
    axios.post(`http://26.90.237.200:3000/admin/house/read/id`, { id: houseId })
      .then(response => {
        setHouse(response.data);
        // console.log(response.data)
      })
      .catch(error => {
        console.error('เกิดข้อผิดพลาดในการดึงข้อมูล:', error);
      });
  };
  
  const loadUserData = (userId) => {
    axios.post(`http://26.90.237.200:3000/user/read/id`, { id: userId })
      .then(response => {
        setUsers(response.data);
        // console.log(response.data)
      })
      .catch(error => {
        console.error(error);
      });
  };

  const generatePdf = async () => {
    try {
      const existingPdfBytes = await fetch(PDF).then(res => res.arrayBuffer())
      const pdfDoc = await PDFDocument.load(existingPdfBytes);
      pdfDoc.registerFontkit(fontkit);
      const fontBytes = await axios.get(THSarabunBold, { responseType: 'arraybuffer' }); 
      const font = await pdfDoc.embedFont(fontBytes.data); 
  
      const page = pdfDoc.getPages()[0];
      let y = 780;
  
      const relatedUser = users[0];
      const relatedHouse = houses[0];
      const date = dayjs();
      const day = date.format('DD');
      const month = date.format('MMMM');
      const year = parseInt(date.format('YYYY')) + 543;
      const formattedDate = `${day}                          ${month}                          ${year}`;

      // page.drawText(`วันที่: ${dayjs().format('YYYY-MM-DD')}`, { x: 50, y: y, size: 13, font, color: rgb(0, 0, 0), });
      page.drawText(` ${relatedUser.user_name} ${relatedUser.user_lastname}`, { x: 140, y: y - 168, size: 13, font, color: rgb(0, 0, 0), });
      page.drawText(` ${relatedUser.user_age} `, { x: 318, y: y - 168, size: 13, font, color: rgb(0, 0, 0), });
      page.drawText(` ${relatedUser.user_phone}`, { x: 430, y: y - 168, size: 13, font, color: rgb(0, 0, 0), });
      page.drawText(`แปลงเลขที่: ${relatedHouse.id}`, { x: 50, y: y - 80, size: 13, font, color: rgb(0, 0, 0), });
      page.drawText(`พื้นที่: ${relatedHouse.land_area} ตรว.`, { x: 50, y: y - 100, size: 13, font, color: rgb(0, 0, 0), });
      page.drawText(`จอง (วันที่ ${dayjs().format('YYYY-MM-DD')}) เป็นเงิน ${relatedHouse.book_price} บาท`, { x: 50, y: y - 120, size: 13, font, color: rgb(0, 0, 0), });
  
      const pdfBytes = await pdfDoc.save();
      const pdfUrl = URL.createObjectURL(new Blob([pdfBytes], { type: 'application/pdf' }));
  
      window.open(pdfUrl, 'blank');
      // navigate('/Booking');
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div className="container">
      <br />
      <div>
        {books.map((book) => (
          <div className="card" key={book.b_id}>
            <div className="content">
              <p>เลขการจอง : {book.b_id}</p>
              {/* แสดงข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง */}
              {users.length > 0 ? (
                users.map((user) => (
                  // แสดงข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง
                  <div key={user.id}>
                    <p>ชื่อ-สกุล : {user.user_name} {user.user_lastname}</p>
                    <p >เบอร์โทร : {user.user_phone}</p>
                    <p>อายุ : {user.user_age}</p>
                  </div>
                  ))) : (<p>ไม่พบข้อมูลผู้ใช้</p>)}
                  
              <p>วันที่จอง : {dayjs (book.date).format('DD-MMMM-YYYY')}</p>
              <p>บ้านเลขที่ : {book.h_id}</p>
              {houses.length > 0 ? (
                houses.map((house) => (
                  // แสดงข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง
                  <div key={house.id}>
                    <p>ขนาดพื้นที่ดิน : {house.land_area} ตารางวา </p>
                    <p>แปลนบ้าน : {house.house_plan} </p>
                    <p>ยอดผ่อนดาว : {house.book_price} บาท</p>
                    <p>ค่าจอง : {house.book_price} บาท</p>
                  </div>
                  ))) : (<p>ไม่พบข้อมูลผู้ใช้</p>)}
         
            </div>
          </div>
        ))}
      </div>
      <br />
      <button type="button" className="btn btn-primary" onClick={generatePdf}>
      บันทึกเป็น PDF
    </button>
    </div>
  );
};

export default Receipt;
