import React,  { useContext, useEffect } from 'react';
import { AuthContext } from '../Login/AuthContext';
// import './ShowHousing.css'
import '../../image//464.jpg';
import useFetch from '../../useFetch';
import { NumericFormat } from 'react-number-format';
import { useNavigate } from 'react-router-dom';

const ShowHousing = () => {
  useEffect(() => {

    // ดึงข้อมูลประเภทบ้าน
    axios.get('http://26.90.237.200:3000/admin/house_type/read')
      .then(response => {
        setTypes(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);
  const Auth = useContext(AuthContext);
const House_API = "http://26.90.237.200:3000/admin/house/read";
const { data } = useFetch(House_API);
const navigate = useNavigate();

// console.log(Auth.isLoggedIn)
        return (

          <div className="container">
              <button className="btn btn-primary" onClick={() => {  navigate('/Housing'); }}> ทั้งหมด </button>
              {types.map((type, idx) => (
        <button className="btn btn-primary" key={idx} onClick={() => { navigate(`/Housing${type.name}`); }}>
          โซน {type.name}
        </button>
      ))}

              <button className="btn btn-warning" onClick={() => {  navigate('/TypeEdit'); }}> แก้ไขประเภท </button>
              { Auth.isLoggedIn && (
             <div className='d-flex justify-content-end'> 
                 <button className="btn btn-primary"  onClick={() => { navigate('/HousingAdd');  }}  > เพิ่ม</button>
             </div>
            )}


          <div className="">
        
            {data.map((val, idx) => (
              <div className="card" key={idx}>
                <div className="content">
                  <p>โซน {val.name}</p>
                  <p>บ้านเลขที่ {val.h_id}</p>
                  <p>แปลนบ้าน {val.house_plan}</p>
                  
                <div className="content">
                  <p>IMG อันนี้จะใส่เป็นรูปบ้านนะแต่ยังไม่ได้ทำ</p>
                </div>
                
                  <p>ขนาดพื้นที่ {val.land_area} ตารางเมตร</p>
                  <NumericFormat
                    value={val.h_price}
                    allowLeadingZeros
                    thousandSeparator=","
                    displayType="text"
                    renderText={(value) => <p>{value} บาท</p>}
                  />
                </div>
    
                <div className="content">
                  <button
                    className="btn btn-primary"
                    onClick={() => {
                      navigate('/Planner');
                    }}
                  >
                    ดูรายละเอียด
                  </button>
                  <br />
                  { Auth.isLoggedIn && (
              <div className="content">
                {/* ส่วนที่ต้องการซ่อนเมื่อยังไม่ได้เข้าสู่ระบบ */}
                <button
                  className="btn btn-warning"
                  onClick={() => {
                    navigate('/HousingEdit');
                  }}
                >
                  แก้ไข
                </button>
                <button
                  className="btn btn-danger"
                  onClick={() => {
                    navigate('/HousingDel');
                  }}
                >
                  ลบ
                </button>
              </div>
              
            )}
                </div>
              </div>
            ))}
          </div>
        </div>
   
        )        

  }

  export default ShowHousing;
