import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { NumericFormat } from 'react-number-format';
import { useNavigate } from 'react-router-dom';

const ShowHousing = () => {
  const Auth = useContext(AuthContext);
  const [houses, setHouses] = useState([]);
  const [types, setTypes] = useState([]);
  const [selectedType, setSelectedType] = useState(null);
  const navigate = useNavigate();
  // const [idToDelete, setIdToDelete] = useState(null);

  useEffect(() => {
    loadHouses();
    loadHouseTypes();
  }, []);

  const loadHouses = () => {
    axios.get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const loadHouseTypes = () => {
    axios.get('http://26.90.237.200:3000/admin/house_zone/read')
      .then(response => {
        setTypes(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleShowAllClick = () => {
    axios.get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouses(response.data);
        setSelectedType(null);
      })
      .catch(error => {
        console.error(error);
      });
  };
  
  const handleTypeClick = (type) => {
    const data = { id: type.hz_id };
    setSelectedType(data);
    axios.post('http://26.90.237.200:3000/admin/house/read/zone', data)
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleDeleteClick = (house) => {
    const confirmDelete = window.confirm('คุณต้องการลบบ้านนี้หรือไม่?');
  
    if (confirmDelete) {
      const data = { id: house.h_id };
      console.log(data);
  
      axios
        .delete('http://26.90.237.200:3000/admin/house/delete/', {
          data: JSON.stringify(data),
          headers: {
            'Content-Type': 'application/json'
          }
        })
        .then(response => {
          alert('ลบบ้านสำเร็จ');
          // ดำเนินการหลังจากลบบ้านสำเร็จ (เช่น อัปเดตรายการบ้าน)
        })
        .catch(error => {
          console.error(error);
          alert('เกิดข้อผิดพลาดในการลบบ้าน');
        });
    }
  };

  return (
    <div className="container">
      <div className="btn-group" role="group" aria-label="Basic radio toggle button group">
        <input type="radio" className="btn-check" name="btnradio" id="showAll" autoComplete="off" checked={!selectedType} onChange={handleShowAllClick} />
        <label className="btn btn-outline-primary" htmlFor="showAll">ทั้งหมด</label>
        {types.map((type, idx) => (
          <React.Fragment key={idx}>
            <input type="radio" className="btn-check" name="btnradio" id={`type${type.hz_id}`} autoComplete="off" checked={selectedType && selectedType.id === type.hz_id} onChange={() => handleTypeClick(type)} />
            <label className="btn btn-outline-primary" htmlFor={`type${type.hz_id}`}>{type.name}</label>
          </React.Fragment>
        ))}
      </div>

      {Auth.isLoggedIn && (
        <div className="d-flex justify-content-end">
          <button className="btn btn-success" onClick={() => { navigate('/HousingAdd'); }}>เพิ่ม</button>
        </div>
      )}
<br/>
      <div>
        {houses.map((house, idx) => (
          <div className="card" key={idx}>
            <div className="content">
              <p>โซน {house.name}</p>
              <p>บ้านเลขที่ {house.h_id}</p>
              <p>แปลนบ้าน {house.house_plan}</p>
              <div className="content">
                <p>IMG อันนี้จะใส่เป็นรูปบ้านนะแต่ยังไม่ได้ทำ</p>
              </div>
              <p>ขนาดพื้นที่ {house.land_area} ตารางวา</p>
              <p>ขนาดพื้นที่ {house.area} ตารางเมตร</p>
              <NumericFormat
                value={house.h_price}
                allowLeadingZeros
                thousandSeparator=","
                displayType="text"
                renderText={(value) => <p>{value} บาท</p>}
              />
            </div>
            <div className="content">
              <button className="btn btn-primary" onClick={() => { navigate('/Planner'); }}>
                ดูรายละเอียด
              </button>
              <br />
              {Auth.isLoggedIn && (
                <div className="content">
                  <button className="btn btn-warning" onClick={() => { navigate('/HousingEdit'); }}>
                    แก้ไข
                  </button>
                  <button className="btn btn-danger" onClick={() => handleDeleteClick(house)}>
                    ลบ
                  </button>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ShowHousing;
