import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const EditBooking = () => {
  const User_API = "http://26.90.237.200:3000/contract/add";
  const navigate = useNavigate();
  const { Bid } = useParams(); // รับค่าพารามิเตอร์ Bid จาก URL
  const [addNumBuySale, setAddNumBuySale] = useState("");
  const [addSubcontract, setAddSubcontract] = useState("");
  const [addPublicService, setAddPublicService] = useState("");


//   b_id,  (รหัสจอง)
// num_buySale, (เลขที่สัญญาจะซื้อจะขายที่ดิน)
// num_subcontract, (เลขที่สัญญาจ้างเหมา)
// num_publicService (เลขที่สัญญาสาธารณูปโภค) 

// เพิ่มข้อมูลสัญญา
// http://26.90.237.200:3000/contract/add
// {
//     "addBookFK": 105,
//     "addNumBuySale": "2222",
//     "addSubcontract": "2222",
//     "addPublicService": "2222"
// }

  const updateUser = async () => {

    const data = {
      addBookFK: Bid,
      addNumBuySale: addNumBuySale,
      addSubcontract: addSubcontract,
      addPublicService: addPublicService,
    };
// console.log(data)
    // ถามผู้ใช้ก่อนทำการแก้ไข
    const confirmUpdate = window.confirm("คุณต้องการทำการแก้ไขข้อมูลหรือไม่?");

    if (confirmUpdate) {
      try {
        const response = await axios.patch(User_API, data);
        console.log(response.data);

        // แจ้งเตือนเมื่อแก้ไขเสร็จสิ้น
        window.alert("แก้ไขข้อมูลสำเร็จ");
        navigate("/Booking");
      } catch (error) {
        console.error(error);

        // แจ้งเตือนเมื่อเกิดข้อผิดพลาดในการแก้ไขข้อมูล
        window.alert("เกิดข้อผิดพลาดในการแก้ไขข้อมูล");
        navigate("/Error");
      }
    }
  };

  return (
    <div className="container">
      <h1>แก้ไขการจอง</h1>

      <form>
        {books.map((val, idx) => (
          <div key={idx}>
            <div className="form-group">
              <label>บ้านเลขที่</label>
              <input
                type="text"
                className="form-control"
                value={newHouseFK || val.h_id}
                onChange={(e) => setNewHouseFK(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label>เลขบัตรประชาชนผู้จอง</label>
              <input
                type="text"
                className="form-control"
                value={newUserFK || val.user_id}
                onChange={(e) => setNewUserFK(e.target.value)}
              />
            </div>
            <br />
          </div>
        ))}
        <button type="button" className="btn btn-primary" onClick={updateUser}>
          บันทึก
        </button>
      </form> 
    </div>
  );
};

export default EditBooking;
