  import React, { useEffect, useState } from 'react';
  import axios from 'axios';
  import { useParams } from "react-router-dom";
  import { PDFDocument, rgb } from 'pdf-lib';
  import dayjs from 'dayjs';
  import fontkit from '@pdf-lib/fontkit';
  import THSarabunBold from '../../fonts/THSarabunBold.ttf'; // ปรับปรุงพาธตามที่เหมาะสม
  
  const Receipt = () => {
    const [books, setBooks] = useState([]);
    const [users, setUsers] = useState([]);
    // const [houses, setHouse] = useState([]);

    const { Bid } = useParams();

    useEffect(() => {
      loadBookingData();
    }, []);

    const loadBookingData = () => {
      axios.post(`http://26.90.237.200:3000/book/read/id`, { id: Bid })
        .then(response => {
          setBooks(response.data);
          // เมื่อได้รับข้อมูลการจองแล้ว ให้โหลดข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง
          if (response.data.length > 0) {
            loadUserData(response.data[0].user_id);
            // loadHouse(response.data[0].h_id);
          }
        })
        .catch(error => {
          console.error(error);
        });
    };
    
    // const loadHouse = (houseId) => {
    //   axios.post(`http://26.90.237.200:3000/admin/house/read/number`, { number: houseId })
    //     .then(response => {
    //       setHouse(response.data);
    //       console.log(response.data)
    //     })
    //     .catch(error => {
    //       console.error('เกิดข้อผิดพลาดในการดึงข้อมูล:', error);
    //     });
    // };
    
    const loadUserData = (userId) => {
      axios.post(`http://26.90.237.200:3000/user/read/id`, { id: userId })
        .then(response => {
          setUsers(response.data);
          // console.log(response.data)
        })
        .catch(error => {
          console.error(error);
        });
    };

    const generatePdf = async () => {
      try {
        const pdfDoc = await PDFDocument.create();
        pdfDoc.registerFontkit(fontkit);
        const fontBytes = await axios.get(THSarabunBold, { responseType: 'arraybuffer' }); // ดึงไฟล์ฟอนต์มาเป็น bytes array
        const font = await pdfDoc.embedFont(fontBytes.data); // ฟอนต์ที่ได้มาเป็น PDFFont
        const page = pdfDoc.addPage([600, 800]);
        let y = 780;

    
        books.forEach((book, idx) => {
          page.drawText(`เลขการจอง: ${book.b_id}`, {
            x: 50,
            y: y,
            size: 12,
            font, // นำ PDFFont ที่ได้มาใช้ในฟังก์ชัน drawText
            color: rgb(0, 0, 0),
          });

          // Draw other text elements here...

          y -= 120;
        });
    
        const pdfBytes = await pdfDoc.save();
        const pdfUrl = URL.createObjectURL(new Blob([pdfBytes], { type: 'application/pdf' }));
    
        // Open a new window to download the PDF
        window.open(pdfUrl, 'blank');
      } catch (error) {
        console.error(error);
      }
    };

    return (
      <div className="container">
        <br />
        <div>
          {books.map((book) => (
            <div className="card" key={book.b_id}>
              <div className="content">
                <p>เลขการจอง : {book.b_id}</p>
                {/* แสดงข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง */}
                {users.length > 0 ? (
                  users.map((user) => (
                    // แสดงข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง
                    <div key={user.id}>
                      <p>ชื่อ-สกุล : {user.user_name} {user.user_lastname}</p>
                      <p>เบอร์โทร : {user.user_phone}</p>
                      <p>อายุ : {user.user_age}</p>
                    </div>
                    ))) : (<p>ไม่พบข้อมูลผู้ใช้</p>)}
                    
                <p>วันที่จอง : {dayjs(book.date).format('YYYY-MM-DD')}</p>
                <p>บ้านเลขที่ : {book.h_id}</p>
                <p>ขนาดพื้นที่ดิน : ตารางวา</p>
                <p>แปลนบ้าน :</p>
                <p>รวมเงินทั้งสิ้น : บาท</p>
              </div>
            </div>
          ))}
        </div>
        <br />
        <button type="button" className="btn btn-primary" onClick={generatePdf}>
        บันทึกเป็น PDF
      </button>
      </div>
    );
  };

  export default Receipt;
