import React, { useEffect, useState,useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
    
const Receipt = () => {
  const [books, setBooks] = useState([]);
  // const navigate = useNavigate();
  const Auth = useContext(AuthContext);

  useEffect(() => {
    loadBooking();
  }, []);

  const handlePrint = () => {
    const selectedBookElements = selectedBooks.map((selectedBookId) => {
      const selectedBook = books.find((book) => book.b_id === selectedBookId);
      return (
        <div key={selectedBook.b_id}>
          <p>เลขการจอง: {selectedBook.b_id}</p>
          <p>ราคา: {selectedBook.amount}</p>
          <p>วันที่จอง: {selectedBook.date}</p>
          <p>บ้านเลขที่: {selectedBook.h_id}</p>
          <p>เลขบัตรประชาชนผู้จอง: {selectedBook.user_id}</p>
          <br />
          <br />
          <br />
        </div>
      );
    });
  
    // Open a new window and write the selectedBookElements to it
    const newWindow = window.open('', '', 'height=500,width=500');
    newWindow.document.write('<html><head><title>Receipt</title></head><body>');
    newWindow.document.write('<h1>Receipt</h1>');
    newWindow.document.write(selectedBookElements);
    newWindow.document.write('</body></html>');
  
    // Print the new window
    newWindow.print();
  };

  return (
    <div className="container">
<br/>
<div>
  {books.map((book, idx) => (
    <div className="card" key={idx}>
      <div className="content">
        <p>เลขการจอง: {book.b_id}</p>
        <p>ราคา: {book.amount}</p>
        <p>วันที่จอง: {book.date}</p>
        {/* <p>สถานะการจอง: {book.status}</p> */}
        <div>
          {/* Add a button to handlePrint when it's clicked */}
          <button
            type="button"
            className="btn btn-primary"
            onClick={() => handlePrint(book.b_id)}
          >
            แก้ไขข้อมูล
          </button>
        </div>
        <p>บ้านเลขที่: {book.h_id}</p>
        <p>เลขบัตรประชาชนผู้จอง: {book.user_id}</p>
      </div>
      <div className="content">
        <br />
        <br />
        <br />
      </div>
    </div>
  ))}
</div>
    </div>
  );
}

export default Receipt;
