import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import React, { useContext,useEffect,useState } from 'react';
import { AuthContext } from '../Login/AuthContext';
import { Dropdown } from 'react-bootstrap';

const getStatus = () => {
  const status = localStorage.getItem('status');
  if (status) return Number(status);
  return null;
};

const NavBar = () => {
  const navigate = useNavigate();
  const Auth = useContext(AuthContext);
  const [status, setStatus] = useState(getStatus()); // เพิ่ม state สำหรับ status

  useEffect(() => {
    setStatus(getStatus());
  }, [Auth.isLoggedIn]); // เมื่อ isLoggedIn เปลี่ยนแปลง อัพเดต status

  const handleLogout = () => {
    localStorage.setItem('loggedIn', 'false');
    localStorage.removeItem('token');
    localStorage.removeItem('status'); // ลบ status ออกจาก localStorage
    Auth.setIsLoggedIn(false);
    setStatus(null); // ลบ status
    navigate('/');
  };

  const handleUserClick = () => {
    navigate('/User');
  };

  const handleProfileClick = () => {
    navigate('/UserProfile');
  };

  return (
    <header>
      <nav className="navbar navbar-expand-lg navbar-dark " style={{ backgroundColor: '#47345F' }}>
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">
          <img src="/image/logo.png" alt="Logo" width="88" height="49" />
          </Link>
  
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav"
            aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav mx-auto">
              <li className="nav-item">
                <Link className="nav-link" to="/">หน้าหลัก</Link>
              </li>
                {Auth.isLoggedIn ? (
    // ส่วนของโค้ดสำหรับผู้ใช้ที่เข้าสู่ระบบแล้ว
    <ul className="navbar-nav mx-auto">
      {Auth.status === 1 && (
        <li className="nav-item">
          <Link className="nav-link" to="/Booking">การจอง</Link>
        </li>
      )}
    </ul>
  ) : null}
              <li className="nav-item">
                <Link className="nav-link" to="/Housing">บ้าน</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/Zoneing">โซน</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/AboutUs">เกี่ยวกับเรา</Link>
              </li>
            </ul>
            <ul className="navbar-nav ms-auto">
              <li className="nav-item me-3">
                {Auth.isLoggedIn ? (
                <p>
      <Dropdown align="end" >
      <Dropdown.Toggle variant="secondary" id="profile-dropdown">Profile</Dropdown.Toggle>
      <Dropdown.Menu>
          {Auth.isLoggedIn && Auth.status === 1 && (
            <div>
        <Dropdown.Item onClick={handleProfileClick}>ข้อมูลผู้จัดการ</Dropdown.Item>
        <Dropdown.Item onClick={handleUserClick}>ข้อมูลลูกค้า</Dropdown.Item>
        </div>
        )}     
        {Auth.isLoggedIn && Auth.status === 3 && (
          <div>
      <Dropdown.Item >แจ้งปัญหา</Dropdown.Item>
      <Dropdown.Item >ข้อมูลค่าส่วนกลาง</Dropdown.Item>
      </div>
      )}
      {Auth.isLoggedIn && Auth.status === 2 && (
          <div>
      <Dropdown.Item >อะไรไม่รูที่อยกาใส่</Dropdown.Item>
      </div>
      )}
        <Dropdown.Divider />
        <Dropdown.Item onClick={handleLogout}>ออกจากระบบ</Dropdown.Item>
      </Dropdown.Menu>
      </Dropdown>
                  </p>
                ) : (
                  <>
                  <button className="btn btn-outline-light"> <Link className="nav-link" to="/LoginForm">เข้าสู่ระบบ</Link> </button>
                  </>
                )}
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </header>
  );
};

export default NavBar;
