import React from 'react';
import './Login.css';
import { useState,} from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = (porps) => {
  const navigate = useNavigate();
  const handleSubmit = e => {

    // Prevent the default submit and page reload
    e.preventDefault()
    if (!username || !password) {
      alert('Please enter both username and password.');
      return;
    }
    const token = localStorage.getItem('token');
    
    // Handle validations
    // Set the request headers with the authentication token
    const config = {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    };

     //Login !!!!!!!==================
    axios.post("http://26.90.237.200:3000/login", { username, password })
    .then(response => {
      const token = response.data.token;
      // Store the token in local storage
      localStorage.setItem('token', token);
      console.log(response.data);
    })
    .catch(error => {
      // Handle the error
      console.error(error);
    });

    //Authen !!!!!!!==================
      axios.post('http://26.90.237.200:3000/authen', { username, password },config)
      
      .then(response => {
        setAuthenStatus(response.data.statusAuthen);
        if (authenStatus == 1) {

          alert('Authen success');

        } else {

          alert('Authen failed');

        }
        // Handle the response data
        console.log(authenStatus);
      })
      
        
      .catch(error => {
        alert('Authen failed')
        // Handle any errors
        console.error(error);
      });

  }

  const [authenStatus, setAuthenStatus] = useState(null);
  const [username, setUsername] = useState()
  const [password, setPassword] = useState()

  const ProtectedComponent = () => {
    return (
      <div>
        <h2>Protected Component</h2>
        <p>This component can only be accessed by authenticated users.</p>
      </div>
    );
  };

  return (
    <div>
      <form action="" id="login" method="post" onSubmit={handleSubmit}>
        <h1>Login</h1>
        <p className="item">
          <label for="username"> Username </label>
          <input
            type="username"
            name="username"
            id="username"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
        </p>
        <p className="item">
          <label for="password"> Password </label>
          <input
            type="password"
            name="password"
            id="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />  
        </p>
        <p className="item">
          <input type="submit" value="Login"   />
        </p>
      </form>
    </div>
  )
  };
  // onClick={() => {navigate('/Housing');}}
  export default Login;
