import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { Link } from 'react-router-dom';
import dayjs from 'dayjs';
import { Modal, Button, Form } from 'react-bootstrap';
import { useNavigate, useParams } from "react-router-dom";

const BookingCard = () => {
  const [books, setBooks] = useState([]);
  const [houses, setHouses] = useState([]);
  // const [selectedType, setSelectedType] = useState(null);
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const User_API = "http://26.90.237.200:3000/transfer/add";
  const [addNote, setAddNote] = useState("");
  const { Bid } = useParams(); // รับค่าพารามิเตอร์ Bid จาก URL
  const navigate = useNavigate();
  const Auth = useContext(AuthContext);

  useEffect(() => {
    loadHouses();
    loadBooking();
  }, []);

  const loadHouses = () => {
    axios
      .get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const loadBooking = () => {
    axios.get('http://26.90.237.200:3000/book/read')
      .then(response => {
        setBooks(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const statusCancel = (status) => {
    switch (status) {
      case 0:
        return { message: 'กำลังดำเนินการจอง', colorClass: 'text-primary' };
      case 1:
        return { message: 'การจองเสร็จสิ้น', colorClass: 'text-success' };
      case 2:
        return { message: 'ยกเลิกการจอง', colorClass: 'text-danger' };
      default:
        return { message: 'ไม่ทราบสถานะ', colorClass: '' };
    }
  };

  const handleCancelClick = (book) => {
    // console.log(book)
    const confirmCancel = window.confirm('คุณต้องการการจองนี้หรือไม่?');
    if (confirmCancel) {
      // const data = { id: book.b_id };
      // console.log(book.b_id)
      axios.patch(`http://26.90.237.200:3000/book/edit/status`,{
          data: { id: book.b_id },
          headers: {
            'Content-Type': 'application/json'
          }
        })
        .then(response => {
          alert('ยกเลิกการจองสำเร็จ');
          loadBooking(); 
          // console.log(data)
        })
        // console.log(data)
        .catch(error => {
          console.error(error);
          alert('เกิดข้อผิดพลาดในการยกเลิกการจอง');
        });
    }
  };

  
  const Contract = async () => {

    const data = {
      addBookFK: Bid,
      addNote: addNote,
    };
console.log(books.b_id)
    // ถามผู้ใช้ก่อนทำการแก้ไข
    const confirmUpdate = window.confirm("คุณต้องการทำการเพิ่มข้อมูลหรือไม่?");

    if (confirmUpdate) {
      try {
        const response = await axios.post(User_API, data);
        console.log(response.data);
        // แจ้งเตือนเมื่อแก้ไขเสร็จสิ้น
        window.alert("เพิ่มข้อมูลสำเร็จ");
        navigate("/Booking");
      } catch (error) {
        console.error(error);
        // แจ้งเตือนเมื่อเกิดข้อผิดพลาดในการแก้ไขข้อมูล
        window.alert("เกิดข้อผิดพลาดเพิ่มข้อมูล");
        navigate("/Error");
      }
    }
  };

  return (
    <div className="container">

<Button variant="primary" onClick={handleShow}>
        บันทึก
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Form Popup</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            {/* ใส่ form fields ที่คุณต้องการที่นี่ */}
            <Form.Group>
              <Form.Label>Field 1</Form.Label>
              <Form.Control type="text" placeholder="Enter text" />
            </Form.Group>
            {/* เพิ่ม fields ตามต้องการ */}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            ปิด
          </Button>
          <Button variant="primary" onClick={handleClose}>
            บันทึก
          </Button>
        </Modal.Footer>
      </Modal>

      {Auth.isLoggedIn && (
        <div className="d-flex justify-content-end">
          <Link to="/AddBooking" className="btn btn-success">เพิ่ม</Link>
        </div>
      )}

      <br />
      <div>
        {books.map((book, idx) => {
          const house = houses.find(house => house.h_id == book.h_id);
          // console.log(houses)
          return (
            <div className="card" key={idx}>
              {Auth.isLoggedIn && (
                <div className="d-flex justify-content-end">
                  {book.status === 0 && (
                    <div className="content">
                      <Link to={`/EditBooking/${book.b_id}`} className="btn btn-warning">แก้ไข</Link>
                    </div>
                  )}
                </div>
              )}
              <div className="content">
                <p>เลขการจอง: {book.b_id}</p>
                {house && (
                
                  <p>ราคาที่จอง: {house.book_price}</p>
                )}
                <p>วันที่จอง: {dayjs(book.date).$d.toString()} </p>
                <div>
                  <div className="content">
                    <p>สถานะการจอง:
                      <span className={`fw-bold ${statusCancel(book.status).colorClass}`}>
                        {statusCancel(book.status).message}
                      </span>
                    </p>
                  </div>
                </div>
                <p>บ้านเลขที่: {book.h_id}</p>
                <p>เลขบัตรประชาชนผู้จอง: {book.user_id}</p>
              </div>
              <div className="content">
              <div className="content">
  {book.status === 0 && (
    <div className="content">
      <Link to={`/ContractAdd/${book.b_id}`} className="btn btn-primary">ทำสัญญา</Link>
      <button className="btn btn-danger" onClick={() => handleCancelClick(book)}>
        ยกเลิกการจอง
      </button>
    </div>
  )}
 
 {house.status !== 3 && (
  <div>
    {book.status === 1 && (
      <div className="content">
        <Link to={`/TransferAdd/${book.b_id}`} className="btn btn-primary">โอนกรรมสิทธิ์</Link>
      </div>
    )}
  </div>
)}
</div>
                <br />
                <br />
                <br />
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default BookingCard;
