import React, { useState } from 'react';
import axios from 'axios';

const HousingEdit = () => {
  const [newName, setNewName] = useState('');
  const [newUsableArea, setNewUsableArea] = useState('');
  const [newStartPrice, setNewStartPrice] = useState('');

  const handleNameChange = (event) => {
    setNewName(event.target.value);
  };

  const handleUsableAreaChange = (event) => {
    setNewUsableArea(event.target.value);
  };

  const handleStartPriceChange = (event) => {
    setNewStartPrice(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // ส่งค่า newName, newUsableArea, newStartPrice ไปยัง API ของคุณด้วย Axios
    axios.put('/api/update', {
      newName: newName,
      newUsableArea: newUsableArea,
      newStartPrice: newStartPrice
    })
    .then((response) => {
      console.log(response.data); // ดำเนินการสำเร็จ
      // เพิ่มโค้ดสำหรับการแจ้งเตือนผู้ใช้ว่าอัปเดตสำเร็จหรือไม่
    })
    .catch((error) => {
      console.error(error); // เกิดข้อผิดพลาดในการส่งคำขอ
      // เพิ่มโค้ดสำหรับการแจ้งเตือนผู้ใช้เกี่ยวกับข้อผิดพลาด
    });
  };
  
  return (
    <div>
      <h1>หน้าอัปเดต</h1>
      <form onSubmit={handleSubmit}>
        <label>
          ชื่อใหม่:
          <input type="text" value={newName} onChange={handleNameChange} />
        </label>
        <br />
        <label>
          พื้นที่ใช้สอยใหม่:
          <input type="text" value={newUsableArea} onChange={handleUsableAreaChange} />
        </label>
        <br />
        <label>
          ราคาเริ่มต้นใหม่:
          <input type="text" value={newStartPrice} onChange={handleStartPriceChange} />
        </label>
        <br />
        <button type="submit">อัปเดต</button>
      </form>
    </div>
  );

};
export default HousingEdit;
