import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { NumericFormat } from 'react-number-format';
import { useNavigate } from 'react-router-dom';

const ShowHousing = () => {
  const Auth = useContext(AuthContext);
  const [houses, setHouses] = useState([]);
  const [types, setTypes] = useState([]);
  const [selectedType, setSelectedType] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });

      
    axios.get('http://26.90.237.200:3000/admin/house_type/read')
      .then(response => {
        setTypes(response.data);
      })
      .catch(error => {
        console.error(error);
      });

  }, []);

  const handleShowAllClick = () => {
    axios.get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouses(response.data);
        setSelectedType(null);
      })
      .catch(error => {
        console.error(error);
      });
  };
  
  const handleTypeClick = (type) => {
    const data = { id: type.ht_id };
    setSelectedType(data);
    console.log(data)
    axios.post('http://26.90.237.200:3000/admin/house/readtype/', data)
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div className="container">
      <div class="btn-group btn-group-toggle" data-toggle="buttons">
  <label class="btn btn-secondary active">
  <input className="btn btn-primary" onClick={handleShowAllClick}>ทั้งหมด</input>
    {/* <input type="radio" name="options" id="option1" autocomplete="off" checked> Active */}
  </label>
  <label class="btn btn-secondary">
    {/* <input type="radio" name="options" id="option2" autocomplete="off"> Radio */}
  </label>
  <label class="btn btn-secondary">
    {/* <input type="radio" name="options" id="option3" autocomplete="off"> Radio */}
  </label>
</div>
     <button className="btn btn-primary" onClick={handleShowAllClick}>ทั้งหมด</button>
      {types.map((type, idx) => (
        <button
        className={`btn btn-primary ${type.ht_id === (selectedType && selectedType.id) ? 'active' : ''}`}
        key={idx} onClick={() => handleTypeClick(type)} >โซน {type.name}</button>
           ))}

      {Auth.isLoggedIn && (
        <div className="d-flex justify-content-end">
          <button className="btn btn-success" onClick={() => { navigate('/HousingAdd'); }}>เพิ่ม</button>
        </div>
      )}

      <div>
        
        {houses.map((house, idx) => (
          <div className="card" key={idx}>
            <div className="content">
              <p>โซน {house.name}</p>
              <p>บ้านเลขที่ {house.h_id}</p>
              <p>แปลนบ้าน {house.house_plan}</p>
              <div className="content">
                <p>IMG อันนี้จะใส่เป็นรูปบ้านนะแต่ยังไม่ได้ทำ</p>
              </div>
              <p>ขนาดพื้นที่ {house.land_area} ตารางเมตร</p>
              <NumericFormat
                value={house.h_price}
                allowLeadingZeros
                thousandSeparator=","
                displayType="text"
                renderText={(value) => <p>{value} บาท</p>}
              />
            </div>
            <div className="content">
              <button
                className="btn btn-primary"
                onClick={() => { navigate('/Planner'); }}
              >
                ดูรายละเอียด
              </button>
              <br />
              {Auth.isLoggedIn && (
                <div className="content">
                  <button
                    className="btn btn-warning"
                    onClick={() => { navigate('/HousingEdit'); }}
                  >
                    แก้ไข
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={() => { navigate('/HousingDel'); }}
                  >
                    ลบ
                  </button>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ShowHousing;
