import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { Link } from 'react-router-dom';
import { useNavigate, useParams } from 'react-router-dom';

const ShowHousing = () => {
  const Auth = useContext(AuthContext);
  const { id } = useParams();
  // const [houses, setHouses] = useState([]);
  const [zones, setZone] = useState([]);
  // const [selectedType, setSelectedType] = useState(null);
  const navigate = useNavigate();
  // const [idToDelete, setIdToDelete] = useState(null);

  useEffect(() => {
    // loadHouses();
    loadHouseTypes();
  }, []);

  // const loadHouses = () => {
  //   axios.get('http://26.90.237.200:3000/admin/house/read')
  //     .then(response => {
  //       setHouses(response.data);
  //     })
  //     .catch(error => {
  //       console.error(error);
  //     });
  // };

  const loadHouseTypes = () => {
    axios.get('http://26.90.237.200:3000/admin/house_zone/read')
      .then(response => {
        setZone(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  // const handleShowAllClick = () => {
  //   axios.get('http://26.90.237.200:3000/admin/house/read')
  //     .then(response => {
  //       setHouses(response.data);
  //       setSelectedType(null);
  //     })
  //     .catch(error => {
  //       console.error(error);
  //     });
  // };
  
  // const handleTypeClick = (zone) => {
  //   const data = { id: zone.hz_id };
  //   setSelectedType(data);
  //   axios.post('http://26.90.237.200:3000/admin/house/read/zone/', data)
  //     .then(response => {
  //       setHouses(response.data);
  //     })
  //     .catch(error => {
  //       console.error(error);
  //     });
  // };

  const handleDeleteClick = (house) => {
    const confirmDelete = window.confirm('คุณต้องการลบโซนนี้หรือไม่?');
    if (confirmDelete) {
      const data = { id: house.hz_id };
      console.log(data);
      axios
        .delete('http://26.90.237.200:3000/admin/house_zone/delete/', {
          data: JSON.stringify(data),
          headers: {
            'Content-Type': 'application/json'
          }
        })
        .then(response => {
          alert('ลบโซนสำเร็จ');
          // ดำเนินการหลังจากลบบ้านสำเร็จ (เช่น อัปเดตรายการบ้าน)
        })
        .catch(error => {
          console.error(error);
          alert('เกิดข้อผิดพลาดในการลบโซน');
        });
    }
  };

  return (
    <div className="container">
       <h1>โซนที่ดิน</h1>
      <div className="btn-group" role="group" aria-label="Basic radio toggle button group">
        {/* <input type="radio" className="btn-check" name="btnradio" id="showAll" autoComplete="off" checked={!selectedType} onChange={handleShowAllClick} /> */}
        {/* <label className="btn btn-outline-primary" htmlFor="showAll">ทั้งหมด</label> */}
        {/* {zones.map((zone, idx) => (
          <React.Fragment key={idx}>
            <input type="radio" className="btn-check" name="btnradio" id={`zone${zone.hz_id}`}
             autoComplete="off" checked={selectedType && selectedType.id === zone.hz_id} onChange={() => handleTypeClick(zone)} />
            <label className="btn btn-outline-primary" htmlFor={`zone${zone.hz_id}`}>{zone.name}</label>
          </React.Fragment>
        ))} */}
      </div>

      {Auth.isLoggedIn && (
        <div className="d-flex justify-content-end">
          <button className="btn btn-success" onClick={() => { navigate('/ZoneAdd'); }}>เพิ่ม</button>
        </div>
      )}
<br/>
      <div>
        {zones.map((zone, idx) => (
          <div className="card" key={idx}>
            <div className="content">
              <p>โซน </p>
              <p> {zone.name}</p>
            
            </div>
            <div className="content">
             
              <br />
              {Auth.isLoggedIn && (
                <div className="content">
                   <Link to={`/ZoneEdit/${zone.hz_id}`} className="btn btn-warning">
                    แก้ไข
                  </Link>
                  <button className="btn btn-danger" onClick={() => handleDeleteClick(zone)}>
                    ลบ
                  </button>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ShowHousing;
