import React, { useContext,useEffect, useState } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
// import { useNavigate } from 'react-router-dom';
const AboutUs = () => {
  const [AboutUs, setAboutUs] = useState([]);
  const Auth = useContext(AuthContext);
//   const navigate = useNavigate();
  useEffect(() => {
    axios.get('http://26.90.237.200:3000/admin/read')
      .then(response => {
        setAboutUs(response.data);
      })
      .catch(error => {
        console.error(error);
      });

  }, []);

  return (
    <div className="container">
      <div>
        {AboutUs.map((val, idx) => (
          <div className="card" key={idx}>
             {Auth.isLoggedIn && (
        <div className="d-flex justify-content-end">
            <Link className="nav-link" to="/Booking">การจอง</Link>
          {/* <button className="btn btn-success" onClick={() => { navigate('/HousingAdd'); }}>เพิ่ม</button> */}
        </div>
      )}
            <div className="content">
           
              <p>โครงการ : {val.name}</p>
              <p>ที่อยู่ : {val.address}</p>
              <p>ชื่อ : {val.manager_name}</p>
              <p>นามสกุล : {val.manager_lastname}</p>
              <p>ชื่อบัญชีผู้ใช้ : {val.he_username}</p>
              <p class="" type="password">รหัสผ่าน : {val.he_password}</p>
              <p> อัตตราผ่อน : {val.down_rate}%</p>
              
              <p> เงินส่วนกลาง : {val.common_money}</p>
            
            </div>
            <div className="content">
              <br />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AboutUs;
