import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import React, { useContext } from 'react';
import { AuthContext } from '../Login/AuthContext';

const NavBar = () => {
  const { status } = useContext(AuthContext);
  const handleLogout = () => {
    localStorage.setItem('loggedIn', 'false');
    localStorage.removeItem('token');
    navigate('/');
  };

  const navigate = useNavigate();

  return (
    <header>
      <nav class="navbar navbar-expand-lg navbar-dark" style={{ backgroundColor: '#47345F' }}>
        <div class="container-fluid">
          <Link class="navbar-brand" to="/">หน้าหลัก</Link>
          <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav"
            aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="navbar-nav">
              <li class="nav-item">
                <Link class="nav-link" to="/Booking">การจอง</Link>
              </li>
              <li class="nav-item">
                <Link class="nav-link" to="/CusData">ข้อมูลลูกค้า</Link>
              </li>
              <li class="nav-item">
                <Link class="nav-link" to="/Housing">โครงการ</Link>
              </li>
            </ul>
          </div>
          <div class="navbar-collapse ms-auto">
            <ul class="navbar-nav">
              <li class="nav-item">
                {status ? (
                  <p>
                    Logged in! <button onClick={handleLogout} class="btn btn-outline-light">Logout</button>
                  </p>
                ) : (
                  <>
                  <div class="container">
                  <button type="button" class="btn btn-primary">  <Link class="nav-link" to="/LoginForm">เข้าสู่ระบบ</Link> </button>
                  <button type="button" class="btn btn-primary">   <Link class="nav-link" to="/Register">สมัครสมาชิก</Link> </button>
                  </div>
               
                  </>
                )}
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </header>
  );
};

export default NavBar;
