import React from 'react';
import './Login.css';
import { useState,} from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = (porps) => {
  const navigate = useNavigate();
  const [authenStatus, setAuthenStatus] = useState(null);
  const handleSubmit = e => {
    e.preventDefault();
    
    if (!username || !password) {
      alert('Please enter both username and password.');
      return;
    }
  
    setAuthenStatus(null); // Reset authenStatus before making API call
    const token = localStorage.getItem('token');
const config = {
  headers: {
    'Authorization': `Bearer ${token}`,
    'Content-Type': 'application/json'
  }
};
  
    axios.post("http://26.90.237.200:3000/login", { username, password })
      .then(response => {
        const token = response.data.token;
        localStorage.setItem('token', token);
        console.log(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  
    axios.post('http://26.90.237.200:3000/authen', { username, password }, config)
      .then(response => {
        setAuthenStatus(response.data.statusAuthen);
        console.log(response.data);
  
        if (authenStatus === 1) {
          // กรณี statusAuthen เป็น 1
          // ทำสิ่งที่ต้องการเมื่อ statusAuthen เป็น 1
          alert('Authen success');
        } else if (authenStatus === 0) {
          // กรณี statusAuthen เป็น 0
          // ทำสิ่งที่ต้องการเมื่อ statusAuthen เป็น 0
          alert('Authen failed');
        } else {
          // กรณี statusAuthen ไม่ใช่ 1 หรือ 0 (ค่าอื่นๆ)
          // ทำสิ่งที่ต้องการเมื่อ statusAuthen ไม่ใช่ 1 หรือ 0
          alert('Invalid statusAuthen');
        }


      })
      .catch(error => {
        alert('Authen failed');
        console.error(error);
      });
  }

  const [username, setUsername] = useState()
  const [password, setPassword] = useState()

  const ProtectedComponent = () => {
    return (
      <div>
        <h2>Protected Component</h2>
        <p>This component can only be accessed by authenticated users.</p>
      </div>
    );
  };

  return (
    <div>
      <form action="" id="login" method="post" onSubmit={handleSubmit}>
        <h1>Login</h1>
        <p className="item">
          <label for="username"> Username </label>
          <input
            type="username"
            name="username"
            id="username"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
        </p>
        <p className="item">
          <label for="password"> Password </label>
          <input
            type="password"
            name="password"
            id="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />  
        </p>
        <p className="item">
          <input type="submit" value="Login"   />
        </p>
      </form>
    </div>
  )
  };
  // onClick={() => {navigate('/Housing');}}
  export default Login;
