import { Link } from 'react-router-dom';
import classes from "./NavBar.module.css";
import { useNavigate } from 'react-router-dom';
import React, { useContext } from 'react';
import { AuthContext } from '../Login/AuthContext';
// icon
// import { BiUserCircle } from "react-icons/bi";
const NavBar = () =>{
    const { status } = useContext(AuthContext);
    
    const handleLogout = () => {
        localStorage.setItem('loggedIn', 'false');
        localStorage.removeItem('token');
        setAuthStatus(false); // อัปเดตค่า status เป็น false
        navigate('/');
      };

    const navigate = useNavigate();
    return (
        <header className={classes.header}>
        <nav>
          <ul className={classes.list}>
            <p>
              <Link to="/" className={classes.item}>หน้าหลัก</Link>
            </p>
            <p>
              <Link to="/Booking" className={classes.item}>การจอง</Link>
            </p>
            <p>
              <Link to="/CusData" className={classes.item}>ข้อมูลลูกค้า</Link>
            </p>
            <p>
              <Link to="/Housing" className={classes.item}>โครงการ</Link>
            </p>
  
            {status ? (
              <p>
                <button onClick={handleLogout}>Logout</button>
              </p>
            ) : (
              <>
                <p>
                  <button className='button' onClick={() => { navigate('/LoginForm'); }}>เข้าสู่ระบบ</button>
                </p>
                <p>
                  <button className='button' onClick={() => { navigate('/Register'); }}>สมัครสมาชิก</button>
                </p>
              </>
            )}
  
          </ul>
          <h2>Other Component</h2>
          <p>Status: {status}</p>
        </nav>
      </header>
        
    );
}
export default NavBar;
