import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { PDFDocument, rgb, StandardFonts } from 'pdf-lib';
import { useParams } from "react-router-dom";
import dayjs from 'dayjs';


const Receipt = () => {
  const [books, setBooks] = useState([]);
  const [user, setUser] = useState([]);

  const { Bid } = useParams();

  useEffect(() => {
    loadBookingData();
    loadUserData();
  }, []);
  
  const userData = books.user_id;

  const loadUserData = () => {
    axios.post(`http://26.90.237.200:3000/user/read/id`,userData)
      .then(response => {
        setUser(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const data = { id: Bid };
// console.log(data);
  const loadBookingData = () => {
    axios.post(`http://26.90.237.200:3000/book/read/id`,data)
      .then(response => {
        setBooks(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };
  
console.log(userData)
  const generatePdf = async () => {
    try {
      const pdfDoc = await PDFDocument.create();
      const page = pdfDoc.addPage([600, 800]);
      let y = 780;
  
      books.forEach((book, idx) => {
        page.drawText(`เลขการจอง: ${book.b_id}`, {
          x: 50,
          y: y,
          size: 12,
          font: StandardFonts, // ใช้ Helvetica-BoldOblique หรือ Times-Roman แทนที่
          color: rgb(0, 0, 0),
        });

        // Draw other text elements here...

        y -= 120;
      });

      const pdfBytes = await pdfDoc.save();
      const pdfUrl = URL.createObjectURL(new Blob([pdfBytes], { type: 'application/pdf' }));

      // Open a new window to download the PDF
      window.open(pdfUrl, 'blank');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    
    <div className="container">
      
      <br/>
      <div>
        {books.map((book, idx) => (
          <div className="card" key={idx}>
            <div className="content">
              <p>เลขการจอง : {book.b_id}</p>
              <p>ชื่อ-สกุล : </p>
              {/* <p>เลขบัตรประชาชนผู้จอง : {book.user_id}</p> */}
              <p>เบอร์โทร : </p>
              <p>อายุ : </p>
              <p>วันที่จอง : {dayjs(book.date).$d.toString()} </p>
              <p>บ้านเลขที่ : {book.h_id}</p>
              <p>ขนาดพื้นที่ดิน : ตารางวา</p>
              <p>แปลนบ้าน :</p>
              <p>รวมเงินทั้งสิ้น : </p>
              
            </div>
          </div>
        ))}
      </div>
<br/>
      <button type="button" className="btn btn-primary" onClick={generatePdf}>
        บันทึกเป็น PDF
      </button>
    </div>
  );
}

export default Receipt;
