import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { useNavigate } from 'react-router-dom';

const ProfileEdit = () => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState([]);
  const Auth = useContext(AuthContext);

  useEffect(() => {
    loadProfile();
  }, []);

  const loadProfile = () => {
    axios
      .get('http://26.90.237.200:3000/admin/read')
      .then(response => {
        setProfile(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleEdit = async () => {
    const updatedProfile = {
      name: document.getElementById('name').value,
      address: document.getElementById('address').value,
      manager_name: document.getElementById('manager_name').value,
      manager_lastname: document.getElementById('manager_lastname').value,
      // เพิ่มข้อมูลอื่น ๆ ตามต้องการ
    };

    try {
      await axios.patch('http://26.90.237.200:3000/admin/edit', updatedProfile);
      navigate('/Profile');
    } catch (error) {
      console.error(error);
      navigate('/Error');
    }
  };

  return (
    <div className="container">
      <form>
        {profile.map((val, idx) => (
          <div key={idx}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">
                ชื่อ
              </label>
              <input
                type="text"
                className="form-control"
                id="name"
                defaultValue={val.name}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="address" className="form-label">
                ที่อยู่
              </label>
              <input
                type="text"
                className="form-control"
                id="address"
                defaultValue={val.address}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="manager_name" className="form-label">
                ชื่อผู้จัดการ
              </label>
              <input
                type="text"
                className="form-control"
                id="manager_name"
                defaultValue={val.manager_name}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="manager_lastname" className="form-label">
                นามสกุลผู้จัดการ
              </label>
              <input
                type="text"
                className="form-control"
                id="manager_lastname"
                defaultValue={val.manager_lastname}
              />
            </div>
        
          </div>
        ))}
        <button type="button" className="btn btn-primary" onClick={handleEdit}>
          บันทึก
        </button>
      </form>
    </div>
  );
};

export default ProfileEdit;
