import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { NumericFormat } from 'react-number-format';
import { useNavigate } from 'react-router-dom';

const ShowHousing = () => {
  const Auth = useContext(AuthContext);
  const [houses, setHouses] = useState([]);
  const [types, setTypes] = useState([]);
  const [selectedTypeId, setSelectedTypeId] = useState(null); // เพิ่มตัวแปร state เพื่อเก็บค่า ht_id ที่ถูกเลือก
  // const [id, setId] = useState('');
  const navigate = useNavigate();

//  console.log(types.ht_id)
  useEffect(() => {
    axios.get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });

    axios.get('http://26.90.237.200:3000/admin/house_type/read')
      .then(response => {
        setTypes(response.data);
        console.log(response.data)
      })
      .catch(error => {
        console.error(error);
      });

      // axios.get('http://26.90.237.200:3000/admin/house/readtype')
      // .then(response => {
      //   setTypes(response.data);
      // })
      // .catch(error => {
      //   console.error(error);
      // });

  }, []);
  const handleTypeClick = (htId) => {
    setSelectedTypeId(htId); // เมื่อโซนถูกคลิก กำหนดค่า ht_id ที่ถูกเลือกให้กับตัวแปร state
  };
  return (
    <div className="container">
         
      <button className="btn btn-primary" onClick={() => { navigate('/Housing'); }}>ทั้งหมด</button>
      {types.map((type, idx) => (
        <button className="btn btn-primary" key={idx} >
          โซน {type.name}
        </button>
      ))}

      {/* ... */}
      {types.map((type, idx) => (
        <button
          className={`btn btn-primary ${type.ht_id === selectedTypeId ? 'active' : ''}`} // เพิ่มคลาส active เมื่อ ht_id ตรงกับ selectedTypeId
          key={idx}
          onClick={() => handleTypeClick(type.ht_id)}
        >
          โซน {type.name}
        </button>
      ))}
      
      {/* ... */}
        {/* {types.map((type, idx) => (
        <button className="btn btn-primary" key={idx} >
          โซน {type.ht_id}
        </button>
        
      ))} */}
{/*       
     {houses.map((house, idx) => (
  <div className="card" key={idx}>
    <div className="content">

      <p>ht_id: {house.ht_id}</p>
    </div>

  </div>
))} */}
      {Auth.isLoggedIn && (
        <button className="btn btn-success" onClick={() => {  navigate('/TypeAdd'); }}> เพิ่ม </button>
      )}
      {Auth.isLoggedIn && (
        <button className="btn btn-warning" onClick={() => {  navigate('/TypeEdit'); }}> แก้ไขประเภท </button>
      )}
      {Auth.isLoggedIn && (
        <button className="btn btn-danger" onClick={() => {  navigate('/TypeDel'); }}> ลบ </button>
      )}
      {Auth.isLoggedIn && (
        <div className="d-flex justify-content-end">
          <button className="btn btn-success" onClick={() => { navigate('/HousingAdd'); }}>เพิ่ม</button>
        </div>
      )}

      <div>
        {houses.map((house, idx) => (
          <div className="card" key={idx}>
            <div className="content">
              <p>โซน {house.name}</p>
              <p>บ้านเลขที่ {house.h_id}</p>
              <p>แปลนบ้าน {house.house_plan}</p>
              <div className="content">
                <p>IMG อันนี้จะใส่เป็นรูปบ้านนะแต่ยังไม่ได้ทำ</p>
              </div>
              <p>ขนาดพื้นที่ {house.land_area} ตารางเมตร</p>
              <NumericFormat
                value={house.h_price}
                allowLeadingZeros
                thousandSeparator=","
                displayType="text"
                renderText={(value) => <p>{value} บาท</p>}
              />
            </div>
            <div className="content">
              <button
                className="btn btn-primary"
                onClick={() => { navigate('/Planner'); }}
              >
                ดูรายละเอียด
              </button>
              <br />
              {Auth.isLoggedIn && (
                <div className="content">
                  <button
                    className="btn btn-warning"
                    onClick={() => { navigate('/HousingEdit'); }}
                  >
                    แก้ไข
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={() => { navigate('/HousingDel'); }}
                  >
                    ลบ
                  </button>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ShowHousing;
