import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { DropdownButton, Dropdown } from 'react-bootstrap';

const HousingEdit = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [newHousePlan, setNewHousePlan] = useState('');
  const [newNumDeed, setNewNumDeed] = useState('');
  const [newLandArea, setNewLandArea] = useState('');
  const [newArea, setNewArea] = useState('');
  const [newPrice, setNewPrice] = useState('');
  const [newNote, setNewNote] = useState('');
  const [newImage, setNewImage] = useState('');
  const [newidFK, setNewidFK] = useState('');

  const [zones, setZones] = useState([]);

  useEffect(() => {
    loadHouseData();
    loadHouseTypes();
  }, []);

  const loadHouseTypes = () => {
    axios
      .get('http://26.90.237.200:3000/admin/house_zone/read')
      .then(response => {
        setZones(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const loadHouseData = () => {
    const data = { number: id };
    axios
      .post('http://26.90.237.200:3000/admin/house/read/number', data)
      .then(response => {
        const houseData = response.data;
        setNewHousePlan(houseData.house_plan || '');
        setNewNumDeed(houseData.num_deed || '');
        setNewLandArea(houseData.land_area || '');
        setNewArea(houseData.area || '');
        setNewPrice(houseData.h_price || '');
        setNewNote(houseData.h_note || '');
        setNewImage(houseData.image || '');
        setNewidFK(houseData.hz_id || '');
        console.log(houseData)
      })
      .catch(error => {
        console.error('เกิดข้อผิดพลาดในการดึงข้อมูล:', error);
      });
  };

  // "id": "112",
  //   "newHousePlan": "test",
  //   "newNumDeed": "test",
  //   "newLandArea": 10,
  //   "newArea": 10,
  //   "newPrice": 10,
  //   "newNote": "test",
  //   "newImage": null,
  //   "newidFK": 2

  const EditHouse = async () => {
    const data = {
      no: id,
      h_id: h_id,
      house_plan: newHousePlan,
      num_deed: newNumDeed,
      land_area: newLandArea,
      area: newArea,
      h_price: newPrice,
      h_note: newNote,
      image: newImage,
      hz_id: newidFK,
    };
    console.log(data)
    try {
      const response = await axios.patch('http://26.90.237.200:3000/admin/edit', data);
      console.log(response.data);
      navigate('/Housing');
    } catch (error) {
      console.error(error);
      navigate('/Error');
    }
  };

  return (
    <div className="container">
      <form>
        <div className="mb-3">
          <div>
            <label htmlFor="id" className="form-label">
              รหัส
            </label>
            <input
              type="text"
              className="form-control"
              id="id"
              value={id}
              readOnly
            />
          </div>
          <div>
            <label htmlFor="housePlan" className="form-label">
              แปลนบ้าน
            </label>
            <input
              type="text"
              className="form-control"
              id="housePlan"
              value={housePlan}
              onChange={e => setNewHousePlan(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="numDeed" className="form-label">
              เลขที่โฉนด
            </label>
            <input
              type="text"
              className="form-control"
              id="numDeed"
              value={numDeed}
              onChange={e => setNewNumDeed(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="landArea" className="form-label">
              ขนาดพื้นที่
            </label>
            <input
              type="number"
              className="form-control"
              id="landArea"
              value={landArea}
              onChange={e => setNewLandArea(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="area" className="form-label">
              พื้นที่ใช้สอย
            </label>
            <input
              type="number"
              className="form-control"
              id="area"
              value={area}
              onChange={e => setNewArea(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="price" className="form-label">
              ราคา
            </label>
            <input
              type="number"
              className="form-control"
              id="price"
              value={price}
              onChange={e => setNewPrice(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="note" className="form-label">
              หมายเหตุ
            </label>
            <input
              type="text"
              className="form-control"
              id="note"
              value={note}
              onChange={e => setNewNote(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="image" className="form-label">
              รูปภาพ
            </label>
            <input
              type="file"
              className="form-control"
              id="image"
              onChange={e => setNewImage(e.target.files[0])}
            />
          </div>
          <div>
            <label htmlFor="zoneId" className="form-label">
              โซนบ้าน
            </label>
            <DropdownButton
              id="zoneId"
              title={zoneId !== '' ? zones.find(zone => zone.hz_id === zoneId)?.name : 'เลือกโซนบ้าน'}
              onSelect={e => setNewidFK(parseInt(e))}
            >
              {zones.map((zone, idx) => (
                <Dropdown.Item key={idx} eventKey={zone.hz_id}>
                  {zone.name}
                </Dropdown.Item>
              ))}
            </DropdownButton>
          </div>
        </div>
        <button type="button" className="btn btn-primary" onClick={EditHouse}>
          แก้ไข
        </button>
      </form>
    </div>
  );
};

export default HousingEdit;
