    import React, { useState, useEffect } from 'react';
    import axios from 'axios';
    import { useNavigate } from "react-router-dom";
    import { Link } from 'react-router-dom';

    const AddBooking = () => {
        const navigate = useNavigate();
        const House_API = 'http://26.90.237.200:3000/book/add';
        const [addedBookData, setAddedBookData] = useState(null);

        const [addName, setAddName] = useState('');
        const [addLastname, setAddLastname] = useState('');
        const [addAddress, setAddAddress] = useState('');
        const [addAge, setAddAge] = useState('');
        const [addPhone, setAddPhone] = useState('');
        const [addHouseFK, setAddHouseFK] = useState('');
        const [addUserFK, setAddUserFK] = useState('');
      
        const addUser = async () => {
            // if (!addName || !addLastname || !addAddress || !addAge || !addPhone || !addHouseFK || !addUserFK) {
            //   alert('กรุณากรอกข้อมูลให้ครบถ้วน');
            //   return;
            // }
        
            // // ตรวจสอบเบอร์โทรศัพท์ที่กรอกเป็นตัวเลขทั้งหมด
            // if (isNaN(addPhone)) {
            //   alert('กรุณากรอกเบอร์โทรศัพท์เป็นตัวเลขเท่านั้น');
            //   return;
            // }
        
            // // แสดงข้อความยืนยันการจอง
            // const confirmBooking = window.confirm('คุณต้องการทำการจองใช่หรือไม่?');
            // if (!confirmBooking) {
            //   return;
            // }
        
            const data = {
              addName: addName,
              addLastname: addLastname,
              addAddress: addAddress,
              addAge: addAge,
              addPhone: addPhone,
              addHouseFK: addHouseFK,
              addUserFK: addUserFK,
            };
            console.log(data);
            try {
              const response = await axios.post(House_API, data);
                setAddedBookData(response.data.id);
                console.log(response.data.id);
              alert('ทำการจองสำเร็จแล้ว');
            //   navigate('/Receipt');
            } catch (error) {
              console.error(error);
              alert('ทำการจองผิดพลาด');
            //   navigate('/Booking');
            }
            // console.log(addedBookData);
          };

          useEffect(() => {
            if (addedBookData) {
              navigate(`/Receipt/${addedBookData}`);
            }
          }, [addedBookData, navigate]);
          
          return (
        
        <div className="container">
               <h1>เพิ่มการจอง</h1>
        <form >
            <div className="mb-3">
                <br/>
            <div className="card container">
            <br/>
                <h4>ข้อมูลลูกค้า</h4>
                <br/>
            <div>
                <label htmlFor="id" className="form-label">
                เลขบัตรประชาชน
                </label>
                <input
                type="text"
                className="form-control"
                id="id"
                value={addUserFK}
                onChange={(e) => setAddUserFK(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="addName" className="form-label">
                ชื่อ
                </label>
                <input
                type="text"
                className="form-control"
                id="addName"
                value={addName}
                onChange={(e) => setAddName(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="addLastname" className="form-label">
                นามสกุล
                </label>
                <input
                type="text"
                className="form-control"
                id="addLastname"
                value={addLastname}
                onChange={(e) => setAddLastname(e.target.value)}
                />
            </div>
            <div >
                <label htmlFor="addAddress" className="form-label">
                ที่อยู่
                </label>
                <input
                type="text"
                className="form-control"
                id="addAddress"
                value={addAddress}
                onChange={(e) => setAddAddress(e.target.value)}
                />
            </div>
            
            <div>
                <label htmlFor="addAge" className="form-label">
                อายุ
                </label>
                <input
                type="text"
                className="form-control"
                id="addAge"
                value={addAge}
                onChange={(e) => setAddAge(e.target.value)}
                />
            </div>

            <div>
                <label htmlFor="addPhone" className="form-label">
                เบอร์โทร
                </label>
                <input
                type="text"
                className="form-control"
                id="addPhone"
                value={addPhone}
                onChange={(e) => setAddPhone(e.target.value)}
                />
            </div>
            <br/>
            </div>
            </div>

            <div className="card container" >
            <br/>
                <h4>ข้อมูลบ้านที่จอง</h4>
                <br/>

            <div className="form-group">
            <label>บ้านเลขที่</label>
            <input
                type="text"
                className="form-control"
                value={addHouseFK}
                onChange={(e) => setAddHouseFK(e.target.value)}
            />
            </div>
            <br/>
          
            </div>
                <div className="content">
                    <Link to={`/Receipt/${addedBookData}`}  onClick={addUser} className="btn btn-warning">  จอง </Link>
                </div>
            <br/>
                    {/* <div className="content">
                      <Link to={`/Receipt/${addedBookData.id}`} onClick={addUser} className="btn btn-warning">  จอง </Link>
                    </div> */}
        </form>
        </div>
    );
    };
    export default AddBooking;
