import React, { useContext, useEffect, useState } from 'react';
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import { AuthContext } from '../Login/AuthContext';

const EditUser = () => {
    const Auth = useContext(AuthContext);
  const User_API = "http://26.90.237.200:3000/user/edit-user";
  const navigate = useNavigate();
  const { Uid } = useParams();  
  const [newEmail, setNewEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");


  const updateUser = async () => {
    const data = {
      id: Uid ,
      newEmail: newEmail ,
      newPassword: newPassword,
    };
// console.log(data)
    // ถามผู้ใช้ก่อนทำการแก้ไข
    const confirmUpdate = window.confirm("คุณต้องการทำการแก้ไขข้อมูลหรือไม่?");

    if (confirmUpdate) {
      try {
        const response = await axios.patch(User_API, data);
        console.log(response.data);

        // แจ้งเตือนเมื่อแก้ไขเสร็จสิ้น
        window.alert("แก้ไขข้อมูลสำเร็จ");
        // navigate("/User/:Uid");
        localStorage.setItem('loggedIn', 'false');
        localStorage.removeItem('token');
        Auth.setIsLoggedIn(false);
        navigate('/');

      } catch (error) {
        console.error(error);

        // แจ้งเตือนเมื่อเกิดข้อผิดพลาดในการแก้ไขข้อมูล
        window.alert("เกิดข้อผิดพลาดในการแก้ไขข้อมูล");
        navigate("/error");
      }
    }
  };

  const statusCancel = (status) => {
    switch (status) {
      case 2:
        return { message: ' ลูกค้า ', colorClass: 'text-success' };
        case 3:
          return { message: ' ลูกบ้าน ', colorClass: 'text-success' };
      default:
        return { message: ' ไม่ทราบสถานะ', colorClass: '' };
    }
  };


  return (
    <div className="container">
      <h1>แก้ไขชื่อผู้ใช้และรหัสผ่าน</h1>
      <form>
            <div className="form-group">
              <label>ชื่อผู้ใช้</label>
              <input
                type="username"
                className="form-control"
                value={newEmail}
                onChange={(e) => setNewEmail(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>รหัสผ่าน</label>
              <input
                type="password"
                className="form-control"
                value={newPassword }
                onChange={(e) => setNewPassword(e.target.value)}
              />
            </div>
            <br />
        <button type="button" className="btn btn-primary" onClick={updateUser}>
          แก้ไขข้อมูล
        </button>
        
      </form>
    </div>
  );
};

export default EditUser;
