import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { DropdownButton, Dropdown } from 'react-bootstrap';

const HousingEdit = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [newHousePlan, setNewHousePlan] = useState('');
  const [newNumDeed, setNewNumDeed] = useState('');
  const [newLandArea, setNewLandArea] = useState('');
  const [newArea, setNewArea] = useState('');
  const [newPrice, setNewPrice] = useState('');
  const [newNote, setNewNote] = useState('');
  const [newImage, setNewImage] = useState('');
  const [newidFK, setNewidFK] = useState('');
  const [zones, setZones] = useState([]);
  const encodedText = encodeURIComponent(text);
  useEffect(() => {
    
    loadHouseTypes();
  }, []);

  const loadHouseTypes = () => {
    axios
      .get('http://26.90.237.200:3000/admin/house_zone/read')
      .then(response => {
        setZones(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  useEffect(() => {
    // ดึงข้อมูลบ้านจาก API โดยใช้ ID ที่รับมาจาก URL parameter
    const data = { 
      // id: id
     }; 
     console.log(data)
    axios.get(`http://26.90.237.200:3000/admin/house/`,data)
      .then(response => {
        
        const house = response.data;
        console.log(house)
        // กำหนดค่าเริ่มต้นให้กับ state ของ input
        setNewHousePlan(house.newHousePlan);
        setNewNumDeed(house.newNumDeed);
        setNewLandArea(house.newLandArea);
        setNewArea(house.newArea);
        setNewPrice(house.newPrice);
        setNewNote(house.newNote);
        setNewImage(house.newImage);
        setNewidFK(house.newidFK);
      })
      .catch(error => {
        console.error(error);
      });
  }, [id]); // รีเรนเดอร์เมื่อ ID เปลี่ยน

  const EditHouse = async () => {
    const data = {
      id: id,
      newHousePlan: newHousePlan,
      newNumDeed: newNumDeed,
      newLandArea: newLandArea,
      newArea: newArea,
      newPrice: newPrice,
      newNote: newNote,
      newImage: newImage,
      newidFK: newidFK,
    };

    try {
      const response = await axios.patch('http://26.90.237.200:3000/admin/house/edit', data);
      console.log(response.data);
      navigate('/Housing');
    } catch (error) {
      console.error(error);
      navigate('/Error');
    }
  };

  return (
    <div className="container">
      <form>
        <div className="mb-3">
          <div>
            <label htmlFor="id" className="form-label">
              รหัส
            </label>
            <input
              type="text"
              className="form-control"
              id="id"
              value={id}
              // onChange={e => setId(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="newHousePlan" className="form-label">
              แปลนบ้าน
            </label>
            <input
              type="text"
              className="form-control"
              id="newHousePlan"
              value={newHousePlan}
              onChange={e => setNewHousePlan(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="newNumDeed" className="form-label">
              เลขที่โฉนด
            </label>
            <input
              type="text"
              className="form-control"
              id="newNumDeed"
              value={newNumDeed}
              onChange={e => setNewNumDeed(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="newLandArea" className="form-label">
              ขนาดพื้นที่
            </label>
            <input
              type="number"
              className="form-control"
              id="newLandArea"
              value={newLandArea}
              onChange={e => setNewLandArea(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="newArea" className="form-label">
              พื้นที่ใช้สอย
            </label>
            <input
              type="number"
              className="form-control"
              id="newArea"
              value={newArea}
              onChange={e => setNewArea(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="newPrice" className="form-label">
              ราคา
            </label>
            <input
              type="number"
              className="form-control"
              id="newPrice"
              value={newPrice}
              onChange={e => setNewPrice(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="newNote" className="form-label">
              หมายเหตุ
            </label>
            <input
              type="text"
              className="form-control"
              id="newNote"
              value={newNote}
              onChange={e => setNewNote(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="newImage" className="form-label">
              รูปภาพ
            </label>
            <input
              type="file"
              className="form-control"
              id="newImage"
              onChange={e => setNewImage(e.target.files[0])}
            />
          </div>
          <div>
            <label htmlFor="newidFK" className="form-label">
              โซนบ้าน
            </label>
            <DropdownButton
              id="newidFK"
              title={newidFK !== '' ? zones.find(zone => zone.hz_id === newidFK)?.name : 'เลือกโซนบ้าน'}
              onSelect={e => setNewidFK(parseInt(e))}
            >
              {zones.map((zone, idx) => (
                <Dropdown.Item key={idx} eventKey={zone.hz_id}>
                  {zone.name}
                </Dropdown.Item>
              ))}
            </DropdownButton>
          </div>
        </div>
        <button type="button" className="btn btn-primary" onClick={EditHouse}>
          แก้ไข
        </button>
      </form>
    </div>
  );
};

export default HousingEdit
