import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const EditBooking = () => {
  const User_API = "http://26.90.237.200:3000/book/edit";
  const navigate = useNavigate();
  const { Bid } = useParams();
  const [newAmount, setNewAmount] = useState("");
  const [newHouseFK, setNewHouseFK] = useState("");
  const [newUserFK, setNewUserFK] = useState("");
  const [books, setBooks] = useState([]);

  useEffect(() => {
    loadBooking();
  }, []);

  // useEffect(() => {
  //   loadBooking();
  // }, []);

  const loadBooking = () => {
    axios.get("http://26.90.237.200:3000/book/read")
      .then((response) => {
        setBooks(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  useEffect(() => {
    // ค้นหาข้อมูลการจองที่ต้องการแก้ไขจาก Bid
    const selectedBooking = books.find((booking) => booking.id === Bid);
    if (selectedBooking) {
      setNewAmount(selectedBooking.amount);
      setNewHouseFK(selectedBooking.house_fk);
      setNewUserFK(selectedBooking.user_fk);
    }
  }, [Bid, books]);


  const updateUser = async () => {
    const data = {
      id: Bid,
      newAmount: newAmount,
      newHouseFK: newHouseFK,
      newUserFK: newUserFK,
    };
console.log(data)
    // ถามผู้ใช้ก่อนทำการแก้ไข
    const confirmUpdate = window.confirm("คุณต้องการทำการแก้ไขข้อมูลหรือไม่?");

    if (confirmUpdate) {
      try {
        const response = await axios.patch(User_API, data);
        console.log(response.data);

        // แจ้งเตือนเมื่อแก้ไขเสร็จสิ้น
        window.alert("แก้ไขข้อมูลสำเร็จ");
        navigate("/BookingCard");
      } catch (error) {
        console.error(error);

        // แจ้งเตือนเมื่อเกิดข้อผิดพลาดในการแก้ไขข้อมูล
        window.alert("เกิดข้อผิดพลาดในการแก้ไขข้อมูล");
        navigate("/BookingCard");
      }
    }
  };

  return (
    <div className="container">
         <h1>แก้ไขการจอง</h1>
      <form>
        {/* <div className="form-group">
          <label>เลขการจอง</label>
          <input
            type="text"
            className="form-control"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </div> */}
        <div className="form-group">
          <label>ราคา</label>
          <input
            type="text"
            className="form-control"
            value={newAmount}
            onChange={(e) => setNewAmount(e.target.value)}
          />
        </div>
      
        <div className="form-group">
          <label>บ้านเลขที่</label>
          <input
            type="text"
            className="form-control"
            value={newHouseFK}
            onChange={(e) => setNewHouseFK(e.target.value)}
          />
        </div>
  
        <div className="form-group">
          <label>เลขบัตรประชาชนผู้จอง</label>
          <input
            type="text"
            className="form-control"
            value={newUserFK}
            onChange={(e) => setNewUserFK(e.target.value)}
          />
        </div>
<br/>
        <button type="button" className="btn btn-primary" onClick={updateUser}>
          แก้ไขข้อมูล
        </button>
      </form>
    </div>
  );
};

export default EditBooking;
