import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { DropdownButton, Dropdown } from 'react-bootstrap';

const HousingEdit = () => {
  const navigate = useNavigate();
  const { Eid } = useParams();
  const [id, setId] = useState('');
  const [housePlan, setHousePlan] = useState('');
  const [numDeed, setNumDeed] = useState('');
  const [landArea, setLandArea] = useState('');
  const [area, setArea] = useState('');
  const [price, setPrice] = useState('');
  const [note, setNote] = useState('');
  const [image, setImage] = useState(null);
  const [zoneId, setZoneId] = useState('');

  const [houseData, setHouseData] = useState([]);
  const [zones, setZones] = useState([]);

  useEffect(() => {
    loadHouseData();
    loadHouseTypes();
  }, []);

  const loadHouseTypes = () => {
    axios.get('http://26.90.237.200:3000/admin/house_zone/read')
      .then(response => {
        setZones(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const loadHouseData = () => {
    const data = { number: Eid };
    axios
      .post('http://26.90.237.200:3000/admin/house/read/number', data)
      .then(response => {
        setHouseData(response.data);
        const house = response.data[0]; // สมมติว่า houseData เป็น array และมีข้อมูลบ้านอยู่ใน index 0
        setId(house.id);
        setHousePlan(house.housePlan);
        setNumDeed(house.numDeed);
        setLandArea(house.landArea);
        setArea(house.area);
        setPrice(house.price);
        setNote(house.note);
        setImage(house.image);
        setZoneId(house.newidFK);
      })
      .catch(error => {
        console.error('เกิดข้อผิดพลาดในการดึงข้อมูล:', error);
      });
  };

  const EditHouse = async () => {
    const data = {
      id: id,
      newHousePlan: housePlan,
      newNumDeed: numDeed,
      newLandArea: landArea,
      newArea: area,
      newPrice: price,
      newNote: note,
      newImage: image,
      newidFK: zoneId,
    };
    console.log(data);

    try {
      const response = await axios.patch('http://26.90.237.200:3000/admin/house/edit',
       {
        data: { data },
        // data: JSON.stringify(data),
        headers: {
          'Content-Type': 'application/json'
        }
      }
      
      );
      console.log(response.data);
      navigate('/Housing');
    } catch (error) {
      console.error(error);
      navigate('/Error');
    }
  };

  console.log(houseData)
  return (
    <div className="container">
      <form>
        <div className="mb-3">
          <div>
            <label htmlFor="id" className="form-label">
              บ้านเลขที่
            </label>
            <input
              type="text"
              className="form-control"
              id="id"
              value={id}
              onChange={e => setId(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="housePlan" className="form-label">
              แปลนบ้าน
            </label>
            <input
              type="text"
              className="form-control"
              id="housePlan"
              value={housePlan}
              onChange={e => setHousePlan(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="numDeed" className="form-label">
              เลขที่โฉนด
            </label>
            <input
              type="text"
              className="form-control"
              id="numDeed"
              value={numDeed}
              onChange={e => setNumDeed(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="landArea" className="form-label">
              ขนาดพื้นที่
            </label>
            <input
              type="number"
              className="form-control"
              id="landArea"
              value={landArea}
              onChange={e => setLandArea(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="area" className="form-label">
              พื้นที่ใช้สอย
            </label>
            <input
              type="number"
              className="form-control"
              id="area"
              value={area}
              onChange={e => setArea(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="price" className="form-label">
              ราคา
            </label>
            <input
              type="number"
              className="form-control"
              id="price"
              value={price}
              onChange={e => setPrice(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label htmlFor="note" className="form-label">
              หมายเหตุ
            </label>
            <input
              type="text"
              className="form-control"
              id="note"
              value={note}
              onChange={e => setNote(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="image" className="form-label">
              รูปภาพ
            </label>
            <input
              type="file"
              className="form-control"
              id="image"
              value={image}
              onChange={e => setImage(e.target.files[0])}
            />
          </div>
          <div>
            <label htmlFor="zoneId" className="form-label">
              โซนบ้าน
            </label>
            <input
              type="text"
              className="form-control"
              id="zoneId"
              value={zoneId}
              onChange={e => setZoneId(e.target.value)}
            />
            <DropdownButton
              id="zoneId"
              title={
                zoneId !== ''
                  ? zones.find(zone => zone.hz_id === zoneId)?.name
                  : 'เลือกโซนบ้าน'
              }
              onSelect={e => setZoneId(parseInt(e))}
            >
              {zones.map((zone, idx) => (
                <Dropdown.Item key={idx} eventKey={zone.hz_id}>
                  {zone.name}
                </Dropdown.Item>
              ))}
            </DropdownButton>
          </div>
        </div>
        <button type="button" className="btn btn-primary" onClick={EditHouse}>
          แก้ไข
        </button>
      </form>
    </div>
  );
};

export default HousingEdit;
