import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const TypeEdit = () => {
  const House_API = "http://26.90.237.200:3000/admin/house_type/edit";
  const navigate = useNavigate();
  
//   {
//     "id": "10",
//     "newHousePlan": "เพชรน้ำเอกพิเศษ",
//     "newNumDeed": "040831",
//     "newLandArea": 85,
//     "newPrice": 3405500,
//     "newNote": "ต่อเติม",
//     "newStatus": 0,
//     "newImage": null,
//     "newidFK": 2
// }
  const [id, setId] = useState("");
  const [newHousePlan, setNewHousePlan] = useState("");
  const [newNumDeed, setNewNumDeed] = useState("");
  const [newLandArea, setNewLandArea] = useState("");
  const [newPrice, setNewPrice] = useState("");
  const [newNote, setNewNote] = useState("");
  const [newStatus, setNewStatus] = useState("");
  const [newImage, setNewImage] = useState("");
  const [newidFK, setNewidFK] = useState("");

  const updateEmp = async () => {
    const data = {
      id: id,
      newHousePlan: newHousePlan,
      newNumDeed: newNumDeed,
      newLandArea: newLandArea,
      newPrice: newPrice,
      newNote: newNote,
      newStatus: newStatus,
      newImage: newImage,
      newidFK: newidFK,
    };

    try {
      const response = await axios.patch(House_API, data);
      console.log(response.data);
      navigate('/Housing');
    } catch (error) {
      console.error(error);
      navigate('/Housing');
    }
  };

  return (
    <div>
      <form>
        <div className="form-group">
          <label>รหัส</label>
          <input
            type="text"
            className="form-control"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>ชื่อแปลนใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newHousePlan}
            onChange={(e) => setNewHousePlan(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>บ้านเลขที่</label>
          <input
            type="text"
            className="form-control"
            value={newNumDeed}
            onChange={(e) => setNewNumDeed(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>พื้นที่บ้านใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newLandArea}
            onChange={(e) => setNewLandArea(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>พื้นที่บ้านใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newLandArea}
            onChange={(e) => setNewLandArea(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>พื้นที่บ้านใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newLandArea}
            onChange={(e) => setNewLandArea(e.target.value)}
          />
        </div>

        <button type="button" className="btn btn-primary" onClick={updateEmp}>
          แก้ไขข้อมูล
        </button>
      </form>
    </div>
  );
};

export default TypeEdit;
