import React, { useState,useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ZoneDel = () => {
  const navigate = useNavigate();
  const [id, setId] = useState('');
  const [types, setTypes] = useState([]);
  useEffect(() => {
    axios.get('http://26.90.237.200:3000/admin/house_type/read')
      .then(response => {
        setTypes(response.data);
      })
      .catch(error => {
        console.error(error);
      });

  }, []);
  const deleteHouse = async () => {
    try {
      const data = { id: id };
      console.log(data)
      const response = await axios.delete('http://26.90.237.200:3000/admin/house_zone/delete', {
        data: JSON.stringify(data),
        headers: {
          'Content-Type': 'application/json'
        }
      }); 
      navigate('/Housing');
      console.log(response.data); 
    } catch (error) {
      console.error(error);
      navigate('/Error');
    }
  };
  return (
    <div className="container">

 {types.map((type, idx) => (
        <button className="btn btn-primary" key={idx} >
          รหัสโซน {type.ht_id}
        </button>
      ))}

      <form>
        <div className="mb-3">
          <label htmlFor="id" className="form-label">
            รหัส
          </label>
          <input
            type="text"
            className="form-control"
            id="id"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </div>
        <button type="button" className="btn btn-danger" onClick={deleteHouse}>
          ลบข้อมูล
        </button>
      </form>
    </div>
  );
};

export default ZoneDel;
