import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const HousingEdit = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [newName, setNewName] = useState('');
  const [newUsableArea, setNewUsableArea] = useState(0);
  const [newStartPrice, setNewStartPrice] = useState(0);

  useEffect(() => {
    axios.get(`http://26.90.237.200:3000/admin/house_type/${ht_id}`)
      .then((response) => {
        const data = response.data;
        setNewName(data.newName);
        setNewUsableArea(data.newUsableArea);
        setNewStartPrice(data.newStartPrice);
      })
      .catch((error) => {
        console.error(error);
      });
  }, [id]);

  const handleUpdate = () => {
    const data = {
      newName: newName,
      newUsableArea: newUsableArea,
      newStartPrice: newStartPrice
    };

    axios.patch(`http://26.90.237.200:3000/admin/house_type/edit/${ht_id}`, data)
      .then((response) => {
        console.log(response.data);
        navigate('/'); // เมื่ออัปเดตเสร็จสิ้น และคุณอาจเปลี่ยนเส้นทางไปยังหน้าอื่นหรือตามที่คุณต้องการ
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <h1>Edit Housing</h1>
      <form onSubmit={handleUpdate}>
        <div>
          <label htmlFor="newName">New Name</label>
          <input
            type="text"
            id="newName"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="newUsableArea">New Usable Area</label>
          <input
            type="number"
            id="newUsableArea"
            value={newUsableArea}
            onChange={(e) => setNewUsableArea(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="newStartPrice">New Start Price</label>
          <input
            type="number"
            id="newStartPrice"
            value={newStartPrice}
            onChange={(e) => setNewStartPrice(e.target.value)}
          />
        </div>
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default HousingEdit;
