import { Link } from 'react-router-dom';
// import classes from "./NavBar.module.css";

import { useNavigate } from 'react-router-dom';
import React, { useContext } from 'react';
import { AuthContext } from '../Login/AuthContext';
// icon
// import { BiUserCircle } from "react-icons/bi";
const NavBar = () =>{
    const { status } = useContext(AuthContext);
    const handleLogout = () => {
        localStorage.setItem('loggedIn', 'false');
        localStorage.removeItem('token');
        navigate('/');
        // สามารถทำการอื่น ๆ เช่น ตั้งค่าต่าง ๆ ในการออกจากระบบที่คุณต้องการ
      };

      
    const navigate = useNavigate();
    return (
        <header>
<nav class="navbar nav justify-content-center bg-dark navbar-dark">
  <div class="container-fluid">
    <ul class="navbar-nav">
      <li class="nav-item">
        <a class="nav-link active" ><Link to="/" >หน้าหลัก</Link></a>
      </li>
     <li class="nav-item">
        <a class="nav-link" >  <Link to="/Booking" >การจอง</Link> </a>
      </li>
      <li class="nav-item">
        <a class="nav-link">   <Link to="/CusData" >ข้อมูลลูกค้า</Link> </a>
      </li>
      <li class="nav-item">
        <a class="nav-link "> <Link to="/Housing">โครงการ</Link> </a>
      </li>
      <li class="nav-item">
        <a class="nav-link " > 
{status ? (
        <p>
          Logged in!{' '}
          <button onClick={handleLogout} >Logout</button>
        </p>
      ) : (
        <>
          <p>
          <button type="button" class="btn btn-outline-primary" onClick={() => { navigate('/LoginForm'); }}>เข้าสู่ระบบ</button>
          </p>
          <p>
          <button type="button" class="btn btn-outline-primary" onClick={() => { navigate('/Register'); }}>สมัครสมาชิก</button>
          </p>
        </>
      )}
</a>
      </li>
    </ul>
  </div>
</nav>
  

      </header>
        
    );
}
export default NavBar;
