import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { Link } from 'react-router-dom';
import dayjs from 'dayjs';
import { useNavigate } from "react-router-dom";
import { Modal, Button, Form } from 'react-bootstrap';
import { NumericFormat } from 'react-number-format';

import 'dayjs/locale/th';
import PDF from './pdf3.pdf'
import THSarabunBold from '../../fonts/THSarabunBold.ttf'; // ปรับปรุงพาธตามที่เหมาะสม
import { PDFDocument, rgb } from 'pdf-lib';

const BookingCard = () => {
  const [books, setBooks] = useState([]);
  const [houses, setHouses] = useState([]);
  // const [selectedType, setSelectedType] = useState(null);
  
  const User_API = "http://26.90.237.200:3000/transfer/add";
  const [addNote, setAddNote] = useState("");
  const [show, setShow] = useState(false);
  const [tempBook, setTempBook] = useState(null);

  const navigate = useNavigate();
  const Auth = useContext(AuthContext);

  useEffect(() => {
    loadHouses();
    loadBooking();
  }, []);

  const loadHouses = () => {
    axios
      .get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const loadBooking = () => {
    axios.get('http://26.90.237.200:3000/book/read')
      .then(response => {
        setBooks(response.data);
      })
      .catch(error => {
